INSERT INTO `physio_exercise` (`id`, `program_id`, `title`, `tipes`, `description`, `equipments`, `rep`, `sets`, `worktime`, `image`, `video_type`, `url_type`, `video_path`, `status`, `update_on`, `image_thumb`, `musclegroup`, `week_no`, `day_no`) VALUES
(2, 1, 'Banded Deadbug', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lie on your back with arms raised above you holding cables/bands. Bend the knees and raise both legs above you. Lower one leg towards the floor keeping tension on the band. Repeat with the other leg.</span></p>', 'Resistance bands', 8, 5, 2, 'uploads/image/1726146354Screenshot_2024_09_12_at_14_04_22.png', 'URL', 'server', 'https://youtu.be/BuYH5C316HY', 'Y', '2024-08-13 13:27:28', 'uploads/thumb/17261463541726146354screenshot_2024_09_12_at_14_04_22.png', 'Abdominals', 29, NULL),
(3, 2, 'Goblet squat', '', '<p><span style=\"color: #222222; font-family: Arial; font-size: 13.3333px; background-color: #ffffff;\">1) Start by holding an appropriately weighted kettlebell close to the chest in a standing position.</span></p>\r\n<p><span style=\"color: #222222; font-family: Arial; font-size: 13.3333px; background-color: #ffffff;\">2) </span><span style=\"color: #222222; font-family: Arial; font-size: 13.3333px; background-color: #ffffff;\">Perform a squat keeping weight through the heels knees over the feet. </span></p>\r\n<p><span style=\"color: #222222; font-family: Arial; font-size: 13.3333px; background-color: #ffffff;\">3) At the bottom phase of the squat tighten the glutes push through the quads and return to the starting position.</span></p>', 'Kettlebell', 5, 3, 5, 'uploads/image/1723560329Screenshot_2024_08_13_at_15_42_56.png', 'URL', 'vimeo', '', 'Y', '2024-08-13 14:45:29', 'uploads/thumb/17235603291723560329screenshot_2024_08_13_at_15_42_56.png', 'All Muscles Used', 29, NULL),
(4, 4, 'Banded Protraction', '', '<p><span style=\"color: #0d0d0d; font-family: Roboto, Noto, sans-serif; font-size: 15px; white-space-collapse: preserve; background-color: #ffffff;\">Using a band coming from behind you, arm at shoulder height. Push your arm away as far as you can and slowly return.</span></p>', 'Resistance bands', 10, 3, 0, 'uploads/image/1726143861Screenshot_2024_09_12_at_13_24_00.png', 'URL', 'server', 'https://youtu.be/HDm62R50BUA', 'Y', '2024-08-28 13:09:12', 'uploads/thumb/17261438611726143861screenshot_2024_09_12_at_13_24_00.png', 'Back', 29, NULL),
(5, 1, '90/90 SIJ Mobility', '', '<p><span style=\"color: #0d0d0d; font-family: Roboto, Noto, sans-serif; font-size: 15px; white-space-collapse: preserve; background-color: #ffffff;\">In the 90 90 position. One hand next to the foot and the other hand next to your knee. Lean forwards keeping your chest up. Rock from side to side. You should feel stretched in the glute and adductors.</span></p>', 'Bodyweight, Bodyweight', 10, 3, 0, 'uploads/image/1726143803Screenshot_2024_09_12_at_13_22_53.png', 'URL', 'server', 'https://youtu.be/9CiAxokQkW4', 'Y', '2024-08-28 13:10:54', 'uploads/thumb/17261438031726143803screenshot_2024_09_12_at_13_22_53.png', 'Back', 28, NULL),
(6, 4, 'Banded Pull Aparts', '', '<p><span style=\"color: #0d0d0d; font-family: Roboto, Noto, sans-serif; font-size: 15px; white-space-collapse: preserve; background-color: #ffffff;\">In standing or sitting. Using a band at shoulder height. Pull it apart so your arms are out straight to the side. Slowly bring to the start and repeat.</span></p>', 'Resistance bands', 0, 0, 0, 'uploads/image/1726143765Screenshot_2024_09_12_at_13_22_19.png', 'URL', 'server', 'https://youtu.be/X-HzhxqQNYU', 'Y', '2024-08-28 13:12:31', 'uploads/thumb/17261437651726143765screenshot_2024_09_12_at_13_22_19.png', 'Shoulders', 28, NULL),
(7, 1, 'Big Toe Mobility', '', '<p><span style=\"color: #0d0d0d; font-family: Roboto, Noto, sans-serif; font-size: 15px; white-space-collapse: preserve; background-color: #ffffff;\">Using the wall or a foam roller. Place your foot on the wall allowing your big toe to point up and the balls of your toes to remain in contact with the floor. Slowly rock back and forwards to stretch the big toe.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726143730Screenshot_2024_09_12_at_13_21_39.png', 'URL', 'server', 'https://youtu.be/QrotICWuMvs', 'Y', '2024-08-28 13:14:22', 'uploads/thumb/17261437301726143730screenshot_2024_09_12_at_13_21_39.png', 'All Muscles Used', 27, NULL),
(8, 4, 'Bottoms Up KB Press', '', '<p><span style=\"color: #0d0d0d; font-family: Roboto, Noto, sans-serif; font-size: 15px; white-space-collapse: preserve; background-color: #ffffff;\">Hold a light kettlebell upside down. Elbow 45 degrees away from the body. Press up until the arm is straight and slowly lower back down. Repeat.</span></p>', 'Kettlebell', 10, 3, 0, 'uploads/image/1726143688Screenshot_2024_09_12_at_13_21_01.png', 'URL', 'server', 'https://youtu.be/vsM5pJvbFXI', 'Y', '2024-08-28 13:15:47', 'uploads/thumb/17261436881726143688screenshot_2024_09_12_at_13_21_01.png', 'Shoulders', 27, NULL),
(9, 4, 'DB Wrist Extension', '', '<p><span style=\"color: #0d0d0d; font-family: Roboto, Noto, sans-serif; font-size: 15px; white-space-collapse: preserve; background-color: #ffffff;\">Hold a dumbbell with the palm facing down. Allow the weight to lower the hand down keeping the forearm still. Raise the hand upwards.</span></p>', 'Dumbbells', 10, 3, 0, 'uploads/image/1726143652Screenshot_2024_09_12_at_13_20_26.png', 'URL', 'server', 'https://youtu.be/W3iZDBSepYs', 'Y', '2024-08-28 13:17:01', 'uploads/thumb/17261436521726143652screenshot_2024_09_12_at_13_20_26.png', 'All Muscles Used', 27, NULL),
(10, 4, 'DB Wrist Flexion', '', '<p><span style=\"color: #0d0d0d; font-family: Roboto, Noto, sans-serif; font-size: 15px; white-space-collapse: preserve; background-color: #ffffff;\">Hold a dumbbell with the palm facing up. Allow the weight to lower the hand down. Raise the hand upwards keeping the forearm still.</span></p>', 'Dumbbells', 10, 3, 0, 'uploads/image/1726143605Screenshot_2024_09_12_at_13_19_37.png', 'URL', 'server', 'https://youtu.be/aOWG4_PEke4', 'Y', '2024-08-28 13:23:07', 'uploads/thumb/17261436051726143605screenshot_2024_09_12_at_13_19_37.png', 'All Muscles Used', 26, NULL),
(11, 3, 'Dynamic Ribcage Mobility SCM', '', '<p><span style=\"color: #0d0d0d; font-family: Roboto, Noto, sans-serif; font-size: 15px; white-space-collapse: preserve; background-color: #ffffff;\">Targeting the SCM here with a nice dynamic ribcage mobility exercise. 1. Start with midfoot pressure on a foam roller or weighted bag 2. Then, lift up slightly off the floor/bench/bed so you can feel your hamstrings, quadriceps, calves and glutes activated 3. Then with a straight arm, reach down your side without moving your shoulder, this will stretch your SCM nicely complete 10 repetitions, each arm, every 2 hours</span></p>', 'Bodyweight, Bodyweight', 10, 3, 0, 'uploads/image/1726143570Screenshot_2024_09_12_at_13_19_03.png', 'URL', 'server', 'https://youtu.be/70SFb3zLj1c', 'Y', '2024-08-28 13:24:39', 'uploads/thumb/17261435701726143570screenshot_2024_09_12_at_13_19_03.png', 'All Muscles Used', 26, NULL),
(12, 2, 'Foam roller bridge with oscillations', '', '<p><span style=\"color: #0d0d0d; font-family: Roboto, Noto, sans-serif; font-size: 15px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on back, midfoot on foam roller. Tilt pelvis back so small of back is in contact with the foam roller. Lift hips up an inch and hold. Lift opposite leg up. Rock foam roller back and forth.</span></p>', 'Workout mat', 12, 3, 0, 'uploads/image/1726143425Screenshot_2024_09_12_at_13_16_29.png', 'URL', 'server', 'https://youtu.be/iYWWrnl0tIE', 'Y', '2024-08-28 13:28:23', 'uploads/thumb/17261434251726143425screenshot_2024_09_12_at_13_16_29.png', 'Hamstrings', 26, NULL),
(13, 5, 'Hip flexor cycling', '', '<p><span style=\"color: #0d0d0d; font-family: Roboto, Noto, sans-serif; font-size: 15px; white-space-collapse: preserve; background-color: #ffffff;\">In long sitting, use your hands to stabilise you. Drag your heel across the floor, lift the leg up to create a cycling motion. Lay flat to make the movement harder.</span></p>', 'Bodyweight, Bodyweight', 10, 3, 0, 'uploads/image/1726143381Screenshot_2024_09_12_at_13_15_49.png', 'URL', 'server', 'https://youtu.be/326xhlZSSKY', 'Y', '2024-08-28 13:29:46', 'uploads/thumb/17261433811726143381screenshot_2024_09_12_at_13_15_49.png', 'All Muscles Used', 26, NULL),
(14, 5, 'Hip Flexor Strengthening', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\" data-sheets-root=\"1\">In long sitting, legs out in front. Place a block/book/item next to one ankle. Lift your leg up and over the item and return. This is 1 rep. Repeat.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726143346Screenshot_2024_09_12_at_13_15_10.png', 'URL', 'server', 'https://youtu.be/kfw_WfEyt5Q', 'Y', '2024-08-29 11:25:31', 'uploads/thumb/17261433461726143346screenshot_2024_09_12_at_13_15_10.png', 'All Muscles Used', 25, NULL),
(15, 6, 'KB Wind Up', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\" data-sheets-root=\"1\">Using a barbell in a squat rack, attach a band onto a kettlebell and slide on to the middle of the barbell. Standing infont wind the the barbell so the kettlebell starts to wind up. Once at the top slowly wind back down. Make sure the movement is coming from the wrists.</span></p>', 'Kettlebell', 8, 3, 0, 'uploads/image/1726143302Screenshot_2024_09_12_at_13_14_33.png', 'URL', 'server', 'https://youtu.be/M44R6n5wlCM', 'Y', '2024-08-29 11:28:02', 'uploads/thumb/17261433021726143302screenshot_2024_09_12_at_13_14_33.png', 'Forearms', 25, NULL),
(16, 2, 'Knee Extension in Split Stance with Resistance Band', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\" data-sheets-root=\"1\">Stand in a split stance with a band around the front leg, pulling forward. Transfer most weight onto the front leg with enough tension through the band to bend the knee. Slowly and gently, straighten the knee against the resistance band. Slowly control the knee forward into a bent position again.</span></p>', 'Resistance bands', 10, 3, 0, 'uploads/image/1726143257Screenshot_2024_09_12_at_13_13_42.png', 'URL', 'server', 'https://youtu.be/dfnZxRdYIKU', 'Y', '2024-08-29 11:35:50', 'uploads/thumb/17261432571726143257screenshot_2024_09_12_at_13_13_42.png', 'All Muscles Used', 25, NULL),
(17, 1, 'Kneeling Lunge Rotations', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\" data-sheets-root=\"1\">Kneel in a side lunge position. Start with body rotated towards the kneeling leg. As you start to rotate away, lift up driving through your hips and squeeze your glutes.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726143212Screenshot_2024_09_12_at_13_12_57.png', 'URL', 'server', 'https://youtu.be/EbUHOCvs8fI', 'Y', '2024-08-29 11:39:10', 'uploads/thumb/17261432121726143212screenshot_2024_09_12_at_13_12_57.png', 'Glutes', 24, NULL),
(18, 5, 'Mud Walk Frontal', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\" data-sheets-root=\"1\">Wrap a resistance band around the forefoot and stand with the knees slightly bent. Keep the body upright with minimal side leaning and step one leg out to the side against the resistance. Follow this with the other leg in the same direction so that you walk laterally.</span></p>', 'Resistance bands', 10, 3, 0, 'uploads/image/1726143166Screenshot_2024_09_12_at_13_12_14.png', 'URL', 'server', 'https://youtu.be/Ern4F1kR5MI', 'Y', '2024-08-29 11:41:28', 'uploads/thumb/17261431661726143166screenshot_2024_09_12_at_13_12_14.png', 'All Muscles Used', 24, NULL),
(19, 5, 'Mud Walk Lateral/Backwards', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\" data-sheets-root=\"1\">Wrap a resistance band around ankles and stand so resistance is applied. Maintaining tension with the band and feet facing forward half squat. While in the half squat position start walking forward maintaining resistance as able. Perform the same movement but backwards.</span></p>', 'Resistance bands', 10, 3, 0, 'uploads/image/1726143125Screenshot_2024_09_12_at_13_11_38.png', 'URL', 'server', 'https://youtu.be/Q1x_2IqsHOc', 'Y', '2024-08-29 11:43:01', 'uploads/thumb/17261431251726143125screenshot_2024_09_12_at_13_11_38.png', 'All Muscles Used', 24, NULL),
(20, 1, 'Foam Roller Bridge with Ankle Pumps', '', '<p><span style=\"color: #0d0d0d; font-family: Roboto, Noto, sans-serif; font-size: 15px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on back, midfoot on foam roller. Tilt pelvis back so small of back is in contact with the foam roller. Lift hips up an inch and hold. Pump the ankle up and down.</span></p>', 'Workout mat', 10, 3, 0, 'uploads/image/1726143466Screenshot_2024_09_12_at_13_17_19.png', 'URL', 'server', 'https://youtu.be/W1i2I7Jt2q4', 'Y', '2024-08-29 11:45:48', 'uploads/thumb/17261434661726143466screenshot_2024_09_12_at_13_17_19.png', 'Hamstrings', 23, NULL),
(21, 4, 'Prone Overhead Reaches', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\" data-sheets-root=\"1\">Lay on front, head down. Roll shoulders back and down. Lift arms up and then torso. Reach above your head then back and to the start position. Repeat.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726140592Screenshot_2024_09_12_at_12_29_20.png', 'URL', 'server', 'https://youtu.be/CRz5RzX4wLI', 'Y', '2024-08-29 11:53:40', 'uploads/thumb/17261405921726140592screenshot_2024_09_12_at_12_29_20.png', 'Shoulders', 23, NULL),
(22, 1, 'Pigeon Stretch', '', '<p><span style=\"color: #0d0d0d; font-family: Roboto, Noto, sans-serif; font-size: 15px; white-space-collapse: preserve; background-color: #ffffff;\">Raise one knee forward in 4 point kneeling. Rotate the lower leg across the body to rest on the floor. Sink the other leg backwards to lower the body to the floor.</span></p>', 'Bodyweight, Bodyweight', 3, 3, 40, 'uploads/image/1726140632Screenshot_2024_09_12_at_12_30_04.png', 'URL', 'server', 'https://youtu.be/SKTL6vHEyHM', 'Y', '2024-08-29 11:56:06', 'uploads/thumb/17261406321726140632screenshot_2024_09_12_at_12_30_04.png', 'Glutes', 23, NULL),
(23, 1, 'Serratus Posterior with Breathing', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\" data-sheets-root=\"1\">In sitting, place one hand on the opposite knee. Take a deep breath in. As you exhale slide your hand down your leg, rotating your body and head. Feel the stretch and return to the start.</span></p>', 'Bodyweight, Bodyweight', 5, 3, 0, 'uploads/image/1726140547Screenshot_2024_09_12_at_12_28_37.png', 'URL', 'server', 'https://youtu.be/BTJ2FsQ7na4', 'Y', '2024-08-29 11:57:56', 'uploads/thumb/17261405471726140547screenshot_2024_09_12_at_12_28_37.png', 'All Muscles Used', 22, NULL),
(24, 4, 'Shoulder CARS', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\" data-sheets-root=\"1\">Lay on your front, head relaxed down. One arm straight out above your head. Lift up and bring the arm down to the slide of the body. Keep off the ground and take the arm back to the starting position.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726140503Screenshot_2024_09_12_at_12_27_56.png', 'URL', 'server', 'https://youtu.be/KaWybXueIp4', 'Y', '2024-08-29 11:59:19', 'uploads/thumb/17261405031726140503screenshot_2024_09_12_at_12_27_56.png', 'Shoulders', 22, NULL),
(25, 1, 'Split Squat', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\" data-sheets-root=\"1\">Standing on one leg with the other foot behind for support. Chin down, chest down reaching to th floor. Weight is in the heel on the way down. Go as far as you can allowing knee over to then press through the midfoot to push yourself back up bringing your hip through then your body up.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726140466Screenshot_2024_09_12_at_12_27_21.png', 'URL', 'server', 'https://youtu.be/rQ7Cz6JCUsY', 'Y', '2024-08-29 12:01:03', 'uploads/thumb/17261404661726140466screenshot_2024_09_12_at_12_27_21.png', 'All Muscles Used', 22, NULL),
(26, 1, 'Squat with Chair', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\" data-sheets-root=\"1\">Stand with a chair behind you. Lower the hips back into a squat so the hips touch the chair.Drive the hips up to standing before you are able to sit down.</span></p>', 'Bodyweight, Bodyweight', 10, 3, 0, 'uploads/image/1726140430Screenshot_2024_09_12_at_12_26_47.png', 'URL', 'server', 'https://youtu.be/r4ySqHu8sug', 'Y', '2024-08-29 12:02:43', 'uploads/thumb/17261404301726140430screenshot_2024_09_12_at_12_26_47.png', 'Quadriceps', 22, NULL),
(27, 1, 'Sumo Squat with Weight', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\" data-sheets-root=\"1\">Stand holding a weight with your arms straight down, with your feet wide and turned out. Lower the hips back into a squat as low as you can get, keeping weight in the whole foot not just the heels. Drive through the whole foot into standing.</span></p>', 'Dumbbells', 10, 3, 0, 'uploads/image/1726140399Screenshot_2024_09_12_at_12_26_15.png', 'URL', 'server', 'https://youtu.be/QyWnNj8mQJY', 'Y', '2024-08-29 12:04:07', 'uploads/thumb/17261403991726140399screenshot_2024_09_12_at_12_26_15.png', 'All Muscles Used', 21, NULL),
(28, 4, 'W Y T Isometric Holds', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\" data-sheets-root=\"1\">Lay on front, head down. Start with arms in a W position, push through fingertips and hold position for 5 seconds. Make a T position pushing through fingertips and hold. Repeat for a Y position. Fingers stay straight, no grabbing into the floor.</span></p>', 'Bodyweight, Bodyweight', 4, 3, 5, 'uploads/image/1726140366Screenshot_2024_09_12_at_12_25_17.png', 'URL', 'server', 'https://youtu.be/vasK1qNU_P4', 'Y', '2024-08-29 12:05:33', 'uploads/thumb/17261403661726140366screenshot_2024_09_12_at_12_25_17.png', 'Shoulders', 21, NULL),
(29, 1, 'Accepting Weight to Floor Frontal', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\" data-sheets-root=\"1\">Stand a little wider than shoulder height. Allow the chin and chest to relax. Slide your hands down on leg, shifting your weight out to the side, making sure you round your back. Push through your midfoot to return to the start.</span></p>', 'Bodyweight, Bodyweight', 6, 3, 0, 'uploads/image/1726140308Screenshot_2024_09_12_at_12_24_42.png', 'URL', 'server', 'https://youtu.be/Zvm8CF1GNME', 'Y', '2024-08-29 13:00:56', 'uploads/thumb/17261403081726140308screenshot_2024_09_12_at_12_24_42.png', 'All Muscles Used', 21, NULL),
(30, 1, 'Single leg reach forwards', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Standing on a box/step on one leg. Hinge at the hips, keeping the spine neutral. Reach forwards with your arms as far as you can. Push through the midfoot to return to the start position.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726140263Screenshot_2024_09_12_at_12_23_54.png', 'URL', 'server', 'https://youtu.be/MslJ3gBM_Ls', 'Y', '2024-09-09 11:01:30', 'uploads/thumb/17261402631726140263screenshot_2024_09_12_at_12_23_54.png', 'All Muscles Used', 21, NULL),
(31, 2, 'Heel Sliders', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Sit on the floor or in a chair with a disc or slippery object under the foot that allows it to slide Bend the knee sliding the heel towards you Slowly allow the heel to slide way from you</span></p>', 'Bodyweight, Bodyweight', 10, 3, 1, 'uploads/image/1726140222Screenshot_2024_09_12_at_12_23_03.png', 'URL', 'server', 'https://youtu.be/4Fcds3HOJcY', 'Y', '2024-09-09 11:04:54', 'uploads/thumb/17261402221726140222screenshot_2024_09_12_at_12_23_03.png', 'Hamstrings', 20, NULL),
(32, 2, 'Step Up', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Stand with a step in front of you Place one foot on the step Drive the leg up into standing with good knee and hip alignment Slowly bend the knee and hip to lower the other foot to the floor Repeat with the other leg</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726140203Screenshot_2024_09_12_at_12_22_30.png', 'URL', 'server', 'https://youtu.be/x-4aqOOjUTw', 'Y', '2024-09-09 11:07:21', 'uploads/thumb/17261402031726140203screenshot_2024_09_12_at_12_22_30.png', 'Quadriceps', 20, NULL),
(33, 1, 'Foot Clock', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Stand on one leg and imagine you are stood in the middle of a clock. Keep your balance and reach the other leg out along the hours of the clock, returning to the centre between each attempt. </span></p>\r\n<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">&nbsp;</span></p>', 'Bodyweight, Bodyweight', 5, 3, 0, 'uploads/image/1726140142Screenshot_2024_09_12_at_12_21_54.png', 'URL', 'server', 'https://youtu.be/w6zkU0LueTo', 'Y', '2024-09-09 11:09:44', 'uploads/thumb/17261401421726140142screenshot_2024_09_12_at_12_21_54.png', 'All Muscles Used', 20, NULL),
(34, 1, 'Standing Extensions', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">One leg slightly infront of the other. Arms up with thumbs pointing behind. Shift your weight forwards onto the front leg as you arch your back.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726140107Screenshot_2024_09_12_at_12_21_23.png', 'URL', 'server', 'https://youtu.be/CXaY0y5WHcQ', 'Y', '2024-09-09 11:11:59', 'uploads/thumb/17261401071726140107screenshot_2024_09_12_at_12_21_23.png', 'All Muscles Used', 20, NULL),
(35, 1, 'Single Leg Sit to Stand', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Sit on the edge of a chair, with one foot, raise of the floor. Place the other foot on the floor, closer to your body and close to the centre of your body. Drive the hips up off the floor with good posture and knee alignment. Slowly lower the hips down onto the chair, ensuring that your knee remains well aligned and gently sit down to the chair.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726140065Screenshot_2024_09_12_at_12_20_31.png', 'URL', 'server', 'https://youtu.be/37EwGBl9hEU', 'Y', '2024-09-09 11:14:33', 'uploads/thumb/17261400651726140065screenshot_2024_09_12_at_12_20_31.png', 'Full Body', 19, NULL),
(36, 1, 'Accepting Weight to Knee Sagittal', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Stand a little wider than shoulder height. Allow the chin and chest to relax. Slide your hands down on leg to the knee, making sure you round your back. Push through your midfoot to return to the start.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726140023Screenshot_2024_09_12_at_12_19_55.png', 'URL', 'server', 'https://youtu.be/BZNtaGrMH68', 'Y', '2024-09-09 11:16:50', 'uploads/thumb/17261400231726140023screenshot_2024_09_12_at_12_19_55.png', 'Full Body', 19, NULL),
(37, 1, 'Lunge Iso Hold High Heel', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">In a lunge position with midfoot on block. Stay upright with ribcage relaxed. Lift heel up and hold. With arm opposite to front leg, reach forwards as you exhale. 5 breaths x 3.</span></p>', 'Bodyweight, Bodyweight', 5, 3, 0, 'uploads/image/1726139988Screenshot_2024_09_12_at_12_19_19.png', 'URL', 'server', 'https://youtu.be/9ZoJ-Z-pBLI', 'Y', '2024-09-09 11:19:07', 'uploads/thumb/17261399881726139988screenshot_2024_09_12_at_12_19_19.png', 'Full Body', 19, NULL),
(38, 3, 'Isometrics for Neck', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Standing or sitting. Use your hand to add resistance in all directions of the head movements. Hold for 3 seconds x 3 each movement.</span></p>', 'Bodyweight, Bodyweight', 3, 3, 3, 'uploads/image/1726139947Screenshot_2024_09_12_at_12_18_33.png', 'URL', 'server', 'https://youtu.be/npTze1J8Cog', 'Y', '2024-09-09 11:22:04', 'uploads/thumb/17261399471726139947screenshot_2024_09_12_at_12_18_33.png', 'All Muscles Used', 19, NULL),
(39, 1, 'Shoulder Co-contraction Protraction/Retraction', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">On your hands and knees, keep intent through fingers and palm. Slight bend in the elbows and push mid back up as high as possible. Slowly lower body, squeezing the shoulde lades together.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726139904Screenshot_2024_09_12_at_12_17_54.png', 'URL', 'server', 'https://youtu.be/mLF2XMPiRlk', 'Y', '2024-09-09 11:24:30', 'uploads/thumb/17261399041726139904screenshot_2024_09_12_at_12_17_54.png', 'All Muscles Used', 18, NULL),
(40, 4, 'Banded Lunge Protractions', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Band attached to a support. In a half kneeling position, grab the band with the opposite hand to the knee that is forward. Arm at shoulder height. Take a deep breath in and as you exhale push your arm forwards as far as possible and slowly return to start.</span></p>', 'Resistance bands', 8, 3, 0, 'uploads/image/1726139866Screenshot_2024_09_12_at_12_17_16.png', 'URL', 'server', 'https://youtu.be/2FZPfTiuCpY', 'Y', '2024-09-09 12:29:41', 'uploads/thumb/17261398661726139866screenshot_2024_09_12_at_12_17_16.png', 'Full Body', 18, NULL),
(41, 4, 'Serratus Rollout', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on your front with your arms stretched out above your head with a foam roller under your wrists. Push through your arms to lift your body up slightly. Take a deep breath in, as you exhale roll the foam roller way from your body keeping your wrists pressed down. Return to the start keep pressure through the roller at all times.</span></p>', 'Workout mat', 8, 3, 0, 'uploads/image/1726139829Screenshot_2024_09_12_at_12_16_37.png', 'URL', 'server', 'https://youtu.be/Sw-IHZc06kU', 'Y', '2024-09-09 12:32:36', 'uploads/thumb/17261398291726139829screenshot_2024_09_12_at_12_16_37.png', 'All Muscles Used', 18, NULL),
(42, 3, 'Resisted Rotations', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Using a band around your head. Hold tightly with your hands and rotate your head the opposite wat to your hands.</span></p>', 'Resistance bands', 5, 3, 3, 'uploads/image/1726139781Screenshot_2024_09_12_at_12_15_45.png', 'URL', 'server', 'https://youtu.be/va5f_Y-D41Q', 'Y', '2024-09-09 12:35:22', 'uploads/thumb/17261397811726139781screenshot_2024_09_12_at_12_15_45.png', 'All Muscles Used', 17, NULL),
(43, 3, 'Resisted Chin Tucks', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">In sitting with a headrest or standing against a wall or using a resistance band Tuck your chin in and push your head backwards into the resistance. Hold for 5 secs and relax x 3.</span></p>', 'Resistance bands', 3, 3, 3, 'uploads/image/1726067201Screenshot_2024_09_11_at_16_06_07.png', 'URL', 'server', 'https://youtu.be/Mis9K_BO_uA', 'Y', '2024-09-09 12:37:25', 'uploads/thumb/17260672011726067201screenshot_2024_09_11_at_16_06_07.png', 'All Muscles Used', 17, NULL),
(44, 1, 'Seated Back with Alternate Reach', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Sitting in a chair. Inahle through the nose, drop the chin down and exhale as you reach down with one arm. Stop inhale then exhale again as you reach lower with your opposite arm. Continue the breathing and reaching to as low as you can. Push through the mid foot to sit back up to the starting position.</span></p>', 'Bodyweight, Bodyweight', 10, 3, 0, 'uploads/image/1726067158Screenshot_2024_09_11_at_16_05_32.png', 'URL', 'server', 'https://youtu.be/7TiylqfVKd8', 'Y', '2024-09-09 12:39:51', 'uploads/thumb/17260671581726067158screenshot_2024_09_11_at_16_05_32.png', 'All Muscles Used', 17, NULL),
(45, 3, 'Neck Mobilisations', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">In sitting or standing, slightly slouch. Creating a circular motion with your head, breath in as you bring your head back making a double chin, breath out as you start to rotate your head forwards.</span></p>', 'Bodyweight, Bodyweight', 5, 3, 0, 'uploads/image/1726067124Screenshot_2024_09_11_at_16_04_54.png', 'URL', 'server', 'https://youtu.be/f8NOO5PSZqc', 'Y', '2024-09-09 12:42:00', 'uploads/thumb/17260671241726067124screenshot_2024_09_11_at_16_04_54.png', 'All Muscles Used', 17, NULL),
(46, 4, 'Isometrics for Elbow', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">In standing or sitting. Bend the elbow to 90 degrees with palm facing up. With your other hand add some resistance to the palp and hold for 5 seconds as you try to bend the elbow more. Repeat but with the resistance hand on the back of the hand whilst you try and straighten your arm. Match the resistance. Each hold should be 5 seconds x 3</span></p>', 'Bodyweight, Bodyweight', 4, 3, 5, 'uploads/image/1726067085Screenshot_2024_09_11_at_16_04_17.png', 'URL', 'server', 'https://youtu.be/hTi9BwL9Bi0', 'Y', '2024-09-09 12:45:01', 'uploads/thumb/17260670851726067085screenshot_2024_09_11_at_16_04_17.png', 'All Muscles Used', 16, NULL),
(47, 6, 'Pin + Load Tricep', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">2 fingers above the elbow crease. Striaght arm at the elbow, pin and load upwards. Bend the elbow. Let go then go back into the pin and load. You can use a barbell, foam roller or ball to create the same affect if you can not reach the tricep.</span></p>', 'Bodyweight, Bodyweight', 10, 3, 0, 'uploads/image/1726067046Screenshot_2024_09_11_at_16_03_38.png', 'URL', 'server', 'https://youtu.be/yssX6ggl1Yk', 'Y', '2024-09-09 12:47:35', 'uploads/thumb/17260670461726067046screenshot_2024_09_11_at_16_03_38.png', 'All Muscles Used', 16, NULL),
(48, 4, 'Pin + Load Bicep', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">2 fingers above the elbow crease. Bend elbow, pin and load upwards. straighten elbow. Let go then go back into the pin and load.</span></p>', 'Bodyweight, Bodyweight', 10, 3, 0, 'uploads/image/1726067007Screenshot_2024_09_11_at_16_02_58.png', 'URL', 'server', 'https://youtu.be/0xpnYNJ9aSQ', 'Y', '2024-09-09 12:51:38', 'uploads/thumb/17260670071726067007screenshot_2024_09_11_at_16_02_58.png', 'All Muscles Used', 16, NULL),
(49, 1, 'Banded Shoulder Protraction/Retraction', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">On your hands and knees, band around the back and secure in the hands. Lift one hand slightly off the ground. Keep tension in the band and push through you hand on the floor to push your body up and away from your hand.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726066965Screenshot_2024_09_11_at_16_02_14.png', 'URL', 'server', 'https://youtu.be/pYUJ76-jUdM', 'Y', '2024-09-09 12:56:10', 'uploads/thumb/17260669651726066965screenshot_2024_09_11_at_16_02_14.png', 'All Muscles Used', 15, NULL),
(50, 4, 'Bicep Curls', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Standing with slight bend in knees, dumbbells in hand, thumbs facing forwards. Lift wrists towards the body, rotating the thumbs out through the movement. Slowly lower the dumbbells to the start position. Keep elbows close to the body throughout the movement.</span></p>', 'Dumbbells', 8, 3, 0, 'uploads/image/1726066924Screenshot_2024_09_11_at_16_01_36.png', 'URL', 'server', 'https://youtu.be/tiJAq_Lb07E', 'Y', '2024-09-09 12:58:40', 'uploads/thumb/17260669241726066924screenshot_2024_09_11_at_16_01_36.png', 'Biceps', 15, NULL),
(51, 4, 'Tricep Pulldown', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Attach a band to a secure frame. Elbow tucked into the side of the body and at 90 degrees Pull down to straighten the arms. Hold and return to the start slowly.</span></p>', 'Resistance bands', 10, 3, 0, 'uploads/image/1726066886Screenshot_2024_09_11_at_16_00_59.png', 'URL', 'server', 'https://youtu.be/nEDhJSPkzFs', 'Y', '2024-09-09 13:01:02', 'uploads/thumb/17260668861726066886screenshot_2024_09_11_at_16_00_59.png', 'Triceps', 15, NULL),
(52, 4, 'KB Rotations', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Using a light kettlebell, lift your arm so your elbow is in line with your shoulder. Elbow at 90 degrees. Keep your arm in that position and rotate your body towards the kettlebell then away.</span></p>', 'Kettlebell', 6, 3, 0, 'uploads/image/1726066848Screenshot_2024_09_11_at_16_00_16.png', 'URL', 'server', 'https://youtu.be/PjbhWGeuY-Q', 'Y', '2024-09-09 13:03:03', 'uploads/thumb/17260668481726066848screenshot_2024_09_11_at_16_00_16.png', 'Shoulders', 14, NULL),
(53, 4, 'Ribcage Mobility', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on your back, feet on the wall, knees and hips at 90 degrees. Arms out at shoulder height. Breath in through the nose and rotate the palms up, breath out through the mouth and rotate the palms to the ground, bringing the ribcage down as far as you can, expelling as much air as possible. 5 breaths x 3</span></p>', 'Bodyweight, Bodyweight', 5, 3, 0, 'uploads/image/1726066807Screenshot_2024_09_11_at_15_59_38.png', 'URL', 'server', 'https://youtu.be/GZ6HqG8710s', 'Y', '2024-09-09 13:05:36', 'uploads/thumb/17260668071726066807screenshot_2024_09_11_at_15_59_38.png', 'All Muscles Used', 14, NULL),
(54, 1, 'Open Ribcage', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Using a band or holding onto a pole. Sit back into the movement allow the band to pull your arms forwards. Drop your chin and take deep breath in through the nose, out through the mouth. You should be able to feel a stretch between the shoulder blades and down the sides. 5 deep breaths x 3</span></p>', 'Resistance bands', 5, 3, 5, 'uploads/image/1726066768Screenshot_2024_09_11_at_15_59_00.png', 'URL', 'server', 'https://youtu.be/xUym_J5lkxY', 'Y', '2024-09-09 13:07:54', 'uploads/thumb/17260667681726066768screenshot_2024_09_11_at_15_59_00.png', 'All Muscles Used', 14, NULL),
(55, 1, 'Seated Back Relaxations', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Sitting in a chair. Inahle through the nose, drop the chin down and exhale as you reach down. Stop inhale then exhale again as you reach lower. Continue the breathing and reaching to as low as you can. Push through the mid foot to sit back up to the starting position.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726066730Screenshot_2024_09_11_at_15_58_24.png', 'URL', 'server', 'https://youtu.be/k5Xlr_1oXbA', 'Y', '2024-09-09 13:09:56', 'uploads/thumb/17260667301726066730screenshot_2024_09_11_at_15_58_24.png', 'All Muscles Used', 14, NULL),
(56, 1, 'Cat/Camel', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">On your hands and knee. Shoulder stacked over wrist, hips stacked through the knees. Inhale through your nose and push through the mid hand to push your back as high as possible. Exhale through the mouth and lower your back down.</span></p>', 'Bodyweight, Bodyweight', 10, 3, 0, 'uploads/image/1726066694Screenshot_2024_09_11_at_15_57_40.png', 'URL', 'server', 'https://youtu.be/envi5vI9wWg', 'Y', '2024-09-09 13:11:56', 'uploads/thumb/17260666941726066694screenshot_2024_09_11_at_15_57_40.png', 'All Muscles Used', 13, NULL),
(57, 1, 'Thread the Needle', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">On hands and knees. Take one hand through and underneath the armpit. Stretch and hold the position for 5 deep breaths. Rotate back open all the way so arm is above you.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726066650Screenshot_2024_09_11_at_15_57_05.png', 'URL', 'server', 'https://youtu.be/UoZeQTUbEVM', 'Y', '2024-09-09 13:13:52', 'uploads/thumb/17260666501726066650screenshot_2024_09_11_at_15_57_05.png', 'Back', 13, NULL),
(58, 1, 'Core Stability March', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Attach a band to a post at shoulder height. Add some resistance on the band with your arms in front of you. Hold this position and march on the spot. </span></p>', 'Resistance bands', 12, 3, 0, 'uploads/image/1726066618Screenshot_2024_09_11_at_15_56_26.png', 'URL', 'server', 'https://youtu.be/_3VVsX-eiSg', 'Y', '2024-09-09 13:15:55', 'uploads/thumb/17260666181726066618screenshot_2024_09_11_at_15_56_26.png', 'All Muscles Used', 13, NULL),
(59, 2, 'Isometrics for Ankle', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Plantar flexion is pushing your toes down - use your opposite foot to add resistance underneath, hold.. Dorsiflexion is lifting your toes towards you - place your opposite foot on top and add resistance. Eversion - turn your foot out and add resistance with the opposite foot. Inversion turn your foot in and add resistance with the opposite foot. Hold each movement for 5 seconds x 3</span></p>', 'Bodyweight, Bodyweight', 3, 3, 5, 'uploads/image/1726066570Screenshot_2024_09_11_at_15_55_44.png', 'URL', 'server', 'https://youtu.be/EliZwOg4cR0', 'Y', '2024-09-09 13:18:21', 'uploads/thumb/17260665701726066570screenshot_2024_09_11_at_15_55_44.png', 'All Muscles Used', 12, NULL),
(60, 1, 'Mermaid Ribcage Mobility Frontal', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Sit in a 90/90 position, the knee that is in front of you is the side you are working on. Lift the arm up in the air as you breath in. Reach as high as you can whilst shifting your ribcage to the side.</span></p>', 'Bodyweight, Bodyweight', 5, 3, 5, 'uploads/image/1726066535Screenshot_2024_09_11_at_15_55_10.png', 'URL', 'server', 'https://youtu.be/gJwZPh-q5WQ', 'Y', '2024-09-09 13:20:47', 'uploads/thumb/17260665351726066535screenshot_2024_09_11_at_15_55_10.png', 'All Muscles Used', 12, NULL),
(61, 1, 'Seated Hip Pullback', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Sit with hips and knees at 90 degrees. Place a foam roller or cushion between the knees. Squeeze and pull one hip back making the foam roller rotate then alternate with the other hip. Repeat.</span></p>', 'Workout mat', 10, 3, 0, 'uploads/image/1726066501Screenshot_2024_09_11_at_15_54_31.png', 'URL', 'server', 'https://youtu.be/MQjnD741HYE', 'Y', '2024-09-09 13:22:45', 'uploads/thumb/17260665011726066501screenshot_2024_09_11_at_15_54_31.png', 'All Muscles Used', 12, NULL),
(62, 5, 'Seated Hip Internal Rotation', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Sit with hips and knees at 90 degrees. Place a foam roller or cushion between the knees. Squeeze and take one foot out to the side as far as possible, returning to the centre. </span></p>', 'Workout mat', 8, 3, 2, 'uploads/image/1726066457Screenshot_2024_09_11_at_15_53_49.png', 'URL', 'server', 'https://youtu.be/Y_N_awGeqbI', 'Y', '2024-09-09 13:24:37', 'uploads/thumb/17260664571726066457screenshot_2024_09_11_at_15_53_49.png', 'All Muscles Used', 11, NULL),
(63, 5, 'Hip IR/ER Mobility', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">In a 90/90 position, stay as upright through the torso as possible. Lift the foot in front of you up as high as possible and lower. Repeat the same movement with the back foot.</span></p>', 'Bodyweight, Bodyweight', 6, 3, 0, 'uploads/image/1726066416Screenshot_2024_09_11_at_15_53_07.png', 'URL', 'server', 'https://youtu.be/PTV4uXHsMuE', 'Y', '2024-09-09 13:26:36', 'uploads/thumb/17260664161726066416screenshot_2024_09_11_at_15_53_07.png', 'All Muscles Used', 11, NULL),
(64, 1, 'PKS + Obliques', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on back, one leg in the air in a running position. Tilt pelvis backwards so small of lower back is in contact with the floor. Push through the midfoot lifting the heel slightly off the surface at the same time as breathing out and reach across to the foot the is pushing into the surface.</span></p>', 'Bodyweight, Bodyweight', 5, 3, 2, 'uploads/image/1726066374Screenshot_2024_09_11_at_15_52_29.png', 'URL', 'server', 'https://youtu.be/NDXDpsXsKmk', 'Y', '2024-09-09 13:28:31', 'uploads/thumb/17260663741726066374screenshot_2024_09_11_at_15_52_29.png', 'All Muscles Used', 11, NULL),
(65, 1, 'PKS', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on back, one leg straight, tilt pelivs back so small of lower back is in contatc with the floor. Push through forefoot lifting hips off the floor an inch. Hold this position for 2 secs and relax. Repeat.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 2, 'uploads/image/1726066239Screenshot_2024_09_11_at_15_50_05.png', 'URL', 'server', 'https://youtu.be/By74SvckC3o', 'Y', '2024-09-10 08:47:19', 'uploads/thumb/17260662391726066239screenshot_2024_09_11_at_15_50_05.png', 'All Muscles Used', 10, NULL),
(66, 1, 'Supine Hip Flexor Mobility', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Laying on your back, band around feet. Lift one knee towards the chest, maintaining a neutral pelvis and slowly lower to starting position.</span></p>', 'Resistance bands', 3, 8, 0, 'uploads/image/1726066278Screenshot_2024_09_11_at_15_50_53.png', 'URL', 'server', 'https://youtu.be/KHK0DsF53_0', 'Y', '2024-09-10 08:49:39', 'uploads/thumb/17260662781726066278screenshot_2024_09_11_at_15_50_53.png', 'All Muscles Used', 10, NULL),
(67, 5, 'Standing Hip Flexor Mobility', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Standing with band either around feet or front foot to knee. Slight bend in stance leg. Lift opposite knee towards ceiling and slowly back down. Maintain a neutral pelvis throughout.</span></p>', 'Resistance bands', 8, 3, 0, 'uploads/image/1726066197Screenshot_2024_09_11_at_15_49_24.png', 'URL', 'server', 'https://youtu.be/XAhzJ1CuA9Y', 'Y', '2024-09-10 08:52:00', 'uploads/thumb/17260661971726066197screenshot_2024_09_11_at_15_49_24.png', 'All Muscles Used', 10, NULL),
(68, 1, 'Hip Weight Shift', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Slight bend in the knee, slight hinge at the hip. Shift the weight to one side sitting the hips slightly back. Push through the midfoot to return to starting position. Can be done with or without weight.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726066135Screenshot_2024_09_11_at_15_48_27.png', 'URL', 'server', 'https://youtu.be/FXG1GuxlTno', 'Y', '2024-09-10 08:54:01', 'uploads/thumb/17260661351726066135screenshot_2024_09_11_at_15_48_27.png', 'All Muscles Used', 10, NULL),
(69, 1, 'Sagittal Lunge with Medial Reach', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Step forwards, squash the an imaginary orange with the mid foot and twist to the opposite side. Step back to the starting position and repeat. </span></p>', 'Bodyweight, Bodyweight', 10, 3, 0, 'uploads/image/1726066097Screenshot_2024_09_11_at_15_47_47.png', 'URL', 'server', 'https://youtu.be/_FmvAJ0AoZg', 'Y', '2024-09-10 08:56:09', 'uploads/thumb/17260660971726066097screenshot_2024_09_11_at_15_47_47.png', 'All Muscles Used', 9, NULL),
(70, 1, 'Sagittal Lunge with Contralateral Reach', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Step forwards, squash the an imaginary orange with the mid foot and reach up and over to the opposite side. Step back to the starting position and repeat. </span></p>', 'Bodyweight, Bodyweight', 10, 3, 0, 'uploads/image/1726066056Screenshot_2024_09_11_at_15_47_06.png', 'URL', 'server', 'https://youtu.be/yx3o2g4sVAo', 'Y', '2024-09-10 08:58:13', 'uploads/thumb/17260660561726066056screenshot_2024_09_11_at_15_47_06.png', 'All Muscles Used', 9, NULL),
(71, 1, 'Side Lying Hip Rocks with Breathing', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on your side, knees and hips slightly bent. Your top hand lift up and over your head. As you inhale through your nose reach with your arm and push your top hip away from your arm so that you are lengthening your side. If the side you are laying on comes off the surface that is ok. Exhale through your mouth untll all the air is out and return to the srting position. 5 breaths x 3</span></p>', 'Bodyweight, Bodyweight', 5, 3, 0, 'uploads/image/1726066019Screenshot_2024_09_11_at_15_46_35.png', 'URL', 'server', 'https://youtu.be/LYpjbT_H37s', 'Y', '2024-09-10 10:03:24', 'uploads/thumb/17260660191726066019screenshot_2024_09_11_at_15_46_35.png', 'All Muscles Used', 9, NULL),
(72, 5, 'Side Lying Hip Internal Rotation', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on your side, knees and hips slightly bent. Place a foam roller between your knees. Squeeze down onto the foam roller, pull your top hip back and lift your foot towards the ceiling. Hold and slowly lower to the starting position.</span></p>\r\n<h6>**Perform Daily**</h6>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726063815Screenshot_2024_09_11_at_15_09_32.png', 'URL', 'server', 'https://youtu.be/Rl9unjMfjRo', 'Y', '2024-09-10 10:05:28', 'uploads/thumb/17260638151726063815screenshot_2024_09_11_at_15_09_32.png', 'All Muscles Used', 8, NULL),
(73, 1, '3D Lunge Transverse', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">In a half kneeling position, lift arms so they are in line with the shoulders. Take a deep breath in through your nose. As you breath out through the mouth rotate your body and return to the cenre. Repeat and rotate the opposite way.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726065976Screenshot_2024_09_11_at_15_45_46.png', 'URL', 'server', 'https://youtu.be/q74HFr5LY2A', 'Y', '2024-09-10 10:07:51', 'uploads/thumb/17260659761726065976screenshot_2024_09_11_at_15_45_46.png', 'All Muscles Used', 8, NULL),
(74, 1, 'Single Leg Bridge', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on your back, tilt you pelvis backwards to flatten your back on the floor. Push through the mid foot to lift the hips off the floor a couple of inch and hold. Slowly lower to starting position.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726065934Screenshot_2024_09_11_at_15_45_06.png', 'URL', 'server', 'https://youtu.be/rUdyb33c11I', 'Y', '2024-09-10 10:09:43', 'uploads/thumb/17260659341726065934screenshot_2024_09_11_at_15_45_06.png', 'All Muscles Used', 8, NULL),
(75, 5, 'Adductor Iso Hold', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on your side with the top foot on a chair/bench, come onto your elbow. Push through your top foot to lift your hips up, hold this position for 3-5 seconds and slowly lower. To make harder lift the bottom let up to meet he top leg. To make easier keep the bottom leg in contact with the floor.</span></p>', 'Bodyweight, Bodyweight', 5, 3, 3, 'uploads/image/1726065898Screenshot_2024_09_11_at_15_44_26.png', 'URL', 'server', 'https://youtu.be/6t8uQAGZs4A', 'Y', '2024-09-10 10:15:36', 'uploads/thumb/17260658981726065898screenshot_2024_09_11_at_15_44_26.png', 'All Muscles Used', 6, NULL),
(76, 5, 'Side Plank Hip Abduction', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on your side, propped up on your elbow. Knees at 90 degree bend. Lift your hips up and simultaneously lift the top knee towards the ceiling. Slowly lower your hips and knee to the starting position and repeat. </span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726065825Screenshot_2024_09_11_at_15_43_17.png', 'URL', 'server', 'https://youtu.be/CQg5pZyQYhE', 'Y', '2024-09-10 10:18:58', 'uploads/thumb/17260658251726065825screenshot_2024_09_11_at_15_43_17.png', 'All Muscles Used', 7, NULL);
INSERT INTO `physio_exercise` (`id`, `program_id`, `title`, `tipes`, `description`, `equipments`, `rep`, `sets`, `worktime`, `image`, `video_type`, `url_type`, `video_path`, `status`, `update_on`, `image_thumb`, `musclegroup`, `week_no`, `day_no`) VALUES
(77, 1, 'Plank Knee Taps', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Perform a plank with feet and elbows shoulder width apart. Hold this position and slowly lower one knee to the floor until you tap the floor. Alternate with the other knee, making sure you do not shift the hips side to side. </span></p>', 'Bodyweight, Bodyweight', 10, 3, 0, 'uploads/image/1726065785Screenshot_2024_09_11_at_15_42_32.png', 'URL', 'server', 'https://youtu.be/UVHnCC5Au1I', 'Y', '2024-09-10 10:20:50', 'uploads/thumb/17260657851726065785screenshot_2024_09_11_at_15_42_32.png', 'All Muscles Used', 7, NULL),
(78, 2, 'Arch Strengthening', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">In standing or sitting put a small amount of weight through the foot you are working on. Pull the balls of your toes backwards to great an arch. Do not scrunch your toes, they should stay flat to the surface. (Think of your foot moving like a catapillar moves).</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726065742Screenshot_2024_09_11_at_15_41_59.png', 'URL', 'server', 'https://youtu.be/GHz8fgd0MjM', 'Y', '2024-09-10 10:22:54', 'uploads/thumb/17260657421726065742screenshot_2024_09_11_at_15_41_59.png', 'All Muscles Used', 7, NULL),
(79, 2, 'Toe Yoga', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">In standing or sitting put a small amount of weight through the foot you are working on. Individually lift the big toe up. Slowly place it down and the lift the other toes up whilst keeping the big toe dow. Repeat for 30secs x 3 </span></p>', 'Bodyweight, Bodyweight', 1, 3, 30, 'uploads/image/1726065690Screenshot_2024_09_11_at_15_40_57.png', 'URL', 'server', 'https://youtu.be/Lbxi6HZBzCc', 'Y', '2024-09-10 10:26:16', 'uploads/thumb/17260656901726065690screenshot_2024_09_11_at_15_40_57.png', 'All Muscles Used', 6, NULL),
(80, 2, 'Pin + Release Post Ankle', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Kneeling down with one leg in front of you, use your thumb to and place pressure at the side of your achilles. Rock forwards to feel a stretch. Let go of your thumb and return to to the start. Repeat this process working on both sides of the achilles. </span></p>', 'Bodyweight, Bodyweight', 2, 3, 10, 'uploads/image/1726065544Screenshot_2024_09_11_at_15_38_28.png', 'URL', 'server', 'https://youtu.be/FwNHcp6B9xE', 'Y', '2024-09-10 10:29:00', 'uploads/thumb/17260655441726065544screenshot_2024_09_11_at_15_38_28.png', 'All Muscles Used', 6, NULL),
(81, 5, 'Aeroplane', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Standing on one leg with your knee slightly bent. Hinge at your hips, keeping your back straight. Push your big toe into the floor and you lift your opposite knee out to the side, rotating at the hip on the stance leg. Slowly return to the starting position. Repeat </span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726065499Screenshot_2024_09_11_at_15_37_48.png', 'URL', 'server', 'https://youtu.be/AoSA3ufdXdw', 'Y', '2024-09-10 10:31:12', 'uploads/thumb/17260654991726065499screenshot_2024_09_11_at_15_37_48.png', 'All Muscles Used', 7, NULL),
(82, 2, 'RDL Knee Drive', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Stand on one leg slightly bent at the knee with the other leg slightly behind for balance. Bend at the hips and sink backwards into it, feeling a stretch in your hamstring and glute. Drive up by roll through your front foot so that you are on your midfoot, drive your opposite knee towards the ceiling and hold this position for 1-2 seconds. </span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726065284Screenshot_2024_09_11_at_15_33_45.png', 'URL', 'server', 'https://youtu.be/JSCukOcWBkQ', 'Y', '2024-09-10 10:33:59', 'uploads/thumb/17260652841726065284screenshot_2024_09_11_at_15_33_45.png', 'All Muscles Used', 7, NULL),
(83, 2, 'Lunge to Knee Drive', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Place one leg behind you wider than shoulder width apart from your front leg. Perform a lunge. As you drive up out of the lunge roll through your front foot so that you are on your midfoot, drive your opposite knee towards the ceiling and hold this position for 1-2 seconds. </span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726065253Screenshot_2024_09_11_at_15_33_09.png', 'URL', 'server', 'https://youtu.be/w0W3dADBK0Q', 'Y', '2024-09-10 10:37:13', 'uploads/thumb/17260652531726065253screenshot_2024_09_11_at_15_33_09.png', 'All Muscles Used', 7, NULL),
(84, 1, 'Lunge with Twist', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lunge starting position. Place most pressure through your foot foot. As you lunge down allow your knee to track over your toes, twist your torso towards the leg in front and reach down. Push through your midfoot to return to the starting position.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726065180Screenshot_2024_09_11_at_15_32_24.png', 'URL', 'server', 'https://youtu.be/py8rnJh9nF4', 'Y', '2024-09-10 10:39:18', 'uploads/thumb/17260651801726065180screenshot_2024_09_11_at_15_32_24.png', 'All Muscles Used', 7, NULL),
(85, 2, 'Hop Stick', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Standing on one leg with the knee slightly bent, hop forwards and pause, keeping your balance. Do not place your other foot down. </span></p>', 'Bodyweight, Bodyweight', 6, 3, 0, 'uploads/image/1726065133Screenshot_2024_09_11_at_15_31_45.png', 'URL', 'server', 'https://youtu.be/VdY2RJzsaVU', 'Y', '2024-09-10 10:41:10', 'uploads/thumb/17260651331726065133screenshot_2024_09_11_at_15_31_45.png', 'All Muscles Used', 5, NULL),
(86, 1, 'Single Leg Squat with Hip Abduction', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Stand side on to the wall. Place a foam roller between your knee and the wall. Bend the knee that is on the foam roller and push into the foam roller with this leg only. From this position perform a small squat as low as you can to where you feel comfortable. Drive through the whole foot to return to the starting position.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726064890Screenshot_2024_09_11_at_15_27_40.png', 'URL', 'server', 'https://youtu.be/x4V-780NN-Y', 'Y', '2024-09-10 10:43:31', 'uploads/thumb/17260648901726064890screenshot_2024_09_11_at_15_27_40.png', 'All Muscles Used', 6, NULL),
(87, 5, 'Side Lying Knee to Knee', '', '<p><span style=\"color: #0d0d0d; font-family: Roboto, Noto, sans-serif; font-size: 15px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on your side place both feet on the wall with so that your knees are at a 90 degree angle and your top foot is inline with your hip. Keeping your hips still, lift your top knee towards the ceiling without your hips rocking backwards. Hold this position and bring your bottom knee to meet. Hold for 2 seconds and slowly lower the bottom knee. Repeat.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726064852Screenshot_2024_09_11_at_15_27_07.png', 'URL', 'server', 'https://youtu.be/7IQ3qDlAZ-s', 'Y', '2024-09-10 10:46:57', 'uploads/thumb/17260648521726064852screenshot_2024_09_11_at_15_27_07.png', 'All Muscles Used', 6, NULL),
(88, 1, '90/90 Breathing', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on your back place both feet on the wall so that your knees are at a 90 degree angle. Take a deep breath in through your nose and exhale through your mouth untill you have exhaled as much air as possible. Repeat the process.</span></p>', 'Bodyweight, Bodyweight', 5, 3, 0, 'uploads/image/1726064808Screenshot_2024_09_11_at_15_26_19.png', 'URL', 'server', 'https://youtu.be/DLRGbua9rRk', 'Y', '2024-09-10 10:49:21', 'uploads/thumb/17260648081726064808screenshot_2024_09_11_at_15_26_19.png', 'All Muscles Used', 5, NULL),
(89, 1, '90/90 Hip Lift with Reach', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on your back place both feet on the wall so that your knees are at a 90 degree angle. Tilt your pelvis backwards to flatten your back on the floor. Lift your hips up 2 inches. Whilst holding this position lift your arms in the air so that your hands are directly above your shoulders. Roll your shoulders off the floor and hold this position. Take a deep breath in through your nose. As you exhale reach your fingertips towards the ceiling. Take 5 deep breaths whilst holding this position and reach further towards the ceiling each time.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726064768Screenshot_2024_09_11_at_15_25_46.png', 'URL', 'server', 'https://youtu.be/JtEg68BVI7s', 'Y', '2024-09-10 10:51:14', 'uploads/thumb/17260647681726064768screenshot_2024_09_11_at_15_25_46.png', 'All Muscles Used', 5, NULL),
(90, 1, 'Knee Rolls', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on your back place both feet on the wall so that your knees are at a 90 degree angle. Tilt your pelvis backwards to flatten your back on the floor. Lift your hips up 2 inches. Whilst holding this position place the foam roller between your knees. Pull one side of your hips down towards the floor whilst squeezing the foam roller, then alternate with the other hip. The foam roller should rotate. If it does not rotate you are more than likely shifting your hips from side to side and not pulling the hip backwards.</span></p>', 'Bodyweight, Bodyweight', 10, 3, 0, 'uploads/image/1726064728Screenshot_2024_09_11_at_15_25_00.png', 'URL', 'server', 'https://youtu.be/JUIL5W2X4Rc', 'Y', '2024-09-10 10:53:41', 'uploads/thumb/17260647281726064728screenshot_2024_09_11_at_15_25_00.png', 'All Muscles Used', 5, NULL),
(91, 1, 'Running Drivers', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on your back place one foot on the wall so that you have a slight bend in the knee when your foot is on the wall, the other leg lift so that your knee is in line with your hip to mimic a running style. Tilt your pelvis backwards to flatten your back on the floor. Lift your hips up 2 inches. Whilst holding this position lift your mid foot off the wall so that you are pushing into the wall via your mid foot. Keeping your hips off the floor drop the wall foot to the floor then straight back up onto the wall. Repeat foot on wall to floor and returning.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726064683Screenshot_2024_09_11_at_15_24_14.png', 'URL', 'server', 'https://youtu.be/yLS8MFY6QP0', 'Y', '2024-09-10 10:56:13', 'uploads/thumb/17260646831726064683screenshot_2024_09_11_at_15_24_14.png', 'All Muscles Used', 4, NULL),
(92, 2, 'Wall Stride', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on your back place one foot on the wall so that you have a slight bend in the knee when your foot is on the wall, the othe leg lift so that your knee is in line with your hip to mimic a running style. Tilt your pelvis backwards to flatten your back on the floor. Lift your hips up 2 inches. Whilst holding this position lift your mid foot off the wall so that you are pushing into the wall via your heel. Quickly roll through your foot, slapping your midfoot onto the wall and hold. Slowly roll through the foot back to the heel and repeat.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726064634Screenshot_2024_09_11_at_15_23_23.png', 'URL', 'server', 'https://youtu.be/UYtpiepPAH4', 'Y', '2024-09-10 10:58:39', 'uploads/thumb/17260646341726064634screenshot_2024_09_11_at_15_23_23.png', 'All Muscles Used', 4, NULL),
(93, 7, 'Banded TX Rotations', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">In a kneeling lunge position, place a foam roller between your front knee and the wall. With the band hold your arms in front at shoulder height. Keeping the arm against the wall still, rotate your torso to stretch the band and slowly return to the start.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726064580Screenshot_2024_09_11_at_15_22_19.png', 'URL', 'server', 'https://youtu.be/3ul-z0R2caM', 'Y', '2024-09-10 11:00:44', 'uploads/thumb/17260645801726064580screenshot_2024_09_11_at_15_22_19.png', 'All Muscles Used', 4, NULL),
(94, 5, 'Hip CARS', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Kneeling facing the wall, hands on the wall at shoulder height. Take the knee out to the side, rotate the hip and bring round to the front and put the foot on the floor. Lift the knee up and take the knee out to the side finishing in the start position. Keep the knee as high as possible throughout the movement.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726064530Screenshot_2024_09_11_at_15_21_36.png', 'URL', 'server', 'https://youtu.be/KC1LPG7oB-4', 'Y', '2024-09-10 11:02:37', 'uploads/thumb/17260645301726064530screenshot_2024_09_11_at_15_21_36.png', 'All Muscles Used', 4, NULL),
(95, 1, 'Deadbug Pushing Hands through Wall', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Lay on your back, tilt you pelvis backwards to flatten your back on the floor. Push through your fingertips on the wall. Knees at 90 degrees. As we breath out we take 1 leg out at a time, keeping the back in contact with the floor at all times.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726064480Screenshot_2024_09_11_at_15_20_52.png', 'URL', 'server', 'https://youtu.be/_P1yW3rTveI', 'Y', '2024-09-10 11:04:39', 'uploads/thumb/17260644801726064480screenshot_2024_09_11_at_15_20_52.png', 'Abdominals', 3, NULL),
(96, 1, 'Split Stand Abdominal Breathing', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">One foot on the wall. Take a deep breath in and reach down with the opposite hand to the stance leg. As you breath out think about bringing the ribcage down on the side you are reaching towards.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726064347Screenshot_2024_09_11_at_15_18_34.png', 'URL', 'server', 'https://youtu.be/RPfST8fVxBg', 'Y', '2024-09-10 11:06:48', 'uploads/thumb/17260643471726064347screenshot_2024_09_11_at_15_18_34.png', 'All Muscles Used', 3, NULL),
(97, 1, 'Squat Against Wall', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Slowly lower yourself so your back is against the wall, allowing knees to shift forwards over toes. Once you have reached as close to 90 degrees as you can hold this position. </span></p>', 'Bodyweight, Bodyweight', 5, 2, 5, 'uploads/image/1726064204Screenshot_2024_09_11_at_15_16_18.png', 'URL', 'server', 'https://youtu.be/5O37icQx12g', 'Y', '2024-09-10 11:08:31', 'uploads/thumb/17260642041726064204screenshot_2024_09_11_at_15_16_18.png', 'All Muscles Used', 2, NULL),
(98, 2, 'Sagittal Heel Raise', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Hands on the wall, shift your weight forwards. Slight bend in the knee. Transfer onto one leg and tap the heel into the floor</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726064163Screenshot_2024_09_11_at_15_15_32.png', 'URL', 'server', 'https://youtu.be/YRPI3Ra8p20', 'Y', '2024-09-10 11:10:22', 'uploads/thumb/17260641631726064163screenshot_2024_09_11_at_15_15_32.png', 'All Muscles Used', 2, NULL),
(99, 6, 'Sagittal Forearm Press Up', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Intent comes from the fingers. Roll through the fingertips into the hand, keeping the elbow straight. Once the palm is on the wall, slowly lower yourself into a press up then push yourself back up</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726064118Screenshot_2024_09_11_at_15_14_45.png', 'URL', 'server', 'https://youtu.be/m0S3zJQ6szE', 'Y', '2024-09-10 11:12:25', 'uploads/thumb/17260641181726064118screenshot_2024_09_11_at_15_14_45.png', 'All Muscles Used', 2, NULL),
(100, 6, 'Forearm Flexors Sagittal Push Off', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Intent comes from the fingers. Roll through the fingers into the hand. Keeping the elbow straight, flick the wrist down to oush off the wall.</span></p>', 'Bodyweight, Bodyweight', 8, 3, 0, 'uploads/image/1726064018Screenshot_2024_09_11_at_15_12_58.png', 'URL', 'server', 'https://youtu.be/TCP17WPVmsw', 'Y', '2024-09-10 11:14:21', 'uploads/thumb/17260640181726064018screenshot_2024_09_11_at_15_12_58.png', 'All Muscles Used', 2, NULL),
(101, 6, 'Tricep Focused Hand Taps', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Pressure is through the mid hand, your elbow is slightly bent whilst up tap the palm against the wall</span></p>', 'Bodyweight', 8, 3, 0, 'uploads/image/1726063938Screenshot_2024_09_11_at_15_11_45.png', 'URL', 'server', 'https://youtu.be/_rHmJuLkJqU', 'Y', '2024-09-10 11:16:09', 'uploads/thumb/17260639381726063938screenshot_2024_09_11_at_15_11_45.png', 'All Muscles Used', 1, 1),
(102, 4, 'Upper Limb Pec Stretch', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Intent comes from the fingers. Push through the fingers to rotate the body. Don\'t turn the body.</span></p>', 'Bodyweight', 0, 3, 0, 'uploads/image/1726063887Screenshot_2024_09_11_at_15_10_50.png', 'URL', 'server', 'https://youtu.be/Of0imp_ZYZs', 'Y', '2024-09-10 11:18:07', 'uploads/thumb/17260638871726063887screenshot_2024_09_11_at_15_10_50.png', 'All Muscles Used', 1, 1),
(103, 8, 'Pin + Load Forearm', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">1/3 up the forearm just before the muscle starts. Pin and load upwards. Extend the wrist. Let go and repeat across the forearm.</span></p>', 'Bodyweight', 6, 3, 0, 'uploads/image/1726156226Screenshot_2024_09_12_at_16_49_28.png', 'URL', 'server', 'https://youtu.be/lfXBRUg1eCI', 'Y', '2024-09-12 15:50:26', 'uploads/thumb/17261562261726156226screenshot_2024_09_12_at_16_49_28.png', 'All Muscles Used', 1, 1),
(104, 1, 'Foam Roller Bridge', '', '<p><span style=\"font-family: Arial; font-size: 13px; white-space-collapse: preserve; background-color: #ffffff;\">Place the foam roller against the wall. Lay on your back with your mid foot on the foam roller so that your heels are not touching the roller. Tilt your pelvis back to allow the bottom of your back to come into contact with the floor. Push through the mid foot and lift your buttocks two inches off the floor. Hold this position for 5-10 seconds and slowly lower back down. Repeat the same sequence for each repetition. N.B. to make this exercise harder move the foam roller away from the wall.</span></p>', 'Bodyweight', 10, 3, 0, 'uploads/image/1726156860Screenshot_2024_09_12_at_17_00_11.png', 'URL', 'server', 'https://youtu.be/KDpz6lxc2iE', 'Y', '2024-09-12 16:01:01', 'uploads/thumb/17261568611726156860screenshot_2024_09_12_at_17_00_11.png', 'All Muscles Used', 1, 1);
INSERT INTO `physio_program` (`id`, `title`, `description`, `image`, `status`, `update_on`, `no_of_week`) VALUES
(1, 'Lower Back Pain', '<div>\r\n<div class=\"m6vS6b PZPZlf\" data-attrid=\"kc:/medicine/disease:normal symptom description\" data-ved=\"2ahUKEwj5lLC754eFAxUWnmMGHSLmBLcQ0UB6BAhQEAQ\">Back pain can have causes that aren\'t due to underlying disease. Examples include overuse such as working out or lifting too much, prolonged sitting and lying down, sleeping in an uncomfortable position or wearing a poorly fitting backpack.</div>\r\n</div>\r\n<p>&nbsp;</p>', 'uploads/image/1726063257IMG_7143_CR2.jpg', 'Y', '2023-05-24 11:27:15', 1),
(2, 'Knee Pain', '<p><strong>Knee pain </strong>can occur due to a number of possible reasons, including Injury, Arthritis, Bursitis, Being overweight, Overusing the knee.<br /><br /></p>', 'uploads/image/1725969205IMG_7176_CR2.jpg', 'Y', '2023-05-24 12:07:10', 1),
(3, 'Neck Pain', '<p><strong>Neck pain</strong> can occur due to a number of possible reasons, Injury, Posture, Other conditions (Arthritis, a herniated disk, or inflammatory disease), Jaw or head problems.</p>', 'uploads/image/1725969223IMG_7126_CR2.jpg', 'Y', '2024-08-28 13:05:23', 1),
(4, 'Shoulder Pain', '<p><strong>Shoulder pain</strong> can occur due to a number of possible reasons, Arthritis, Frozen shoulder, Rotator cuff injury, Nerve injury, Poor posture, Bone spurs etc.</p>', 'uploads/image/1725969145IMG_7152_CR2.jpg', 'Y', '2024-08-28 13:05:56', 1),
(5, 'Hip Pain', '<p><strong>Hip pain</strong> is a symptom of several conditions, including arthritis, injuries to your hip (fractures, labral tears and dislocation), bursitis and structural issues. Athletes who move their hips in all directions, like dancers and gymnasts, are more likely to injure their hips and have hip pain.</p>', 'uploads/image/1725969121IMG_7164_CR2.jpg', 'Y', '2024-08-28 13:06:45', 1),
(6, 'Wrist Pain', '<p><strong>Wrist pain</strong> is often caused by sprains or fractures from sudden injuries. But wrist pain also can result from long-term problems, such as repetitive stress, arthritis and carpal tunnel syndrome.</p>', 'uploads/image/1725969073IMG_7195_CR2.jpg', 'Y', '2024-08-29 11:26:48', 1),
(7, 'Mid Back Pain', '', 'uploads/image/1725968932IMG_7128_CR2.jpg', 'Y', '2024-09-10 11:48:52', 1),
(8, 'Elbow Pain', '<p><strong>Elbow Pain: </strong>Occurs from overuse causing pain from tennis or golfer\'s elbow, bursitis, tendonitis</p>', 'uploads/image/1725969352IMG_7208_CR2.jpg', 'Y', '2024-09-10 11:55:52', 1),
(9, 'Toe Pain', '<p><strong>Toe Pain:&nbsp;</strong>Can occur due to trauma, arthritis, weakness in the musculature.</p>', 'uploads/image/1725969467IMG_7265_CR2.jpg', 'Y', '2024-09-10 11:57:47', 1),
(10, 'Ankle Pain', '<p><strong>Ankle Pain:</strong> Occurs due to injury such as rolling the ankle, fractures, inflammation, dislocations.</p>', 'uploads/image/1725969544IMG_7180_CR2.jpg', 'Y', '2024-09-10 11:59:04', 1),
(11, 'Chronic Lower Back Pain', '', 'uploads/image/1725969616IMG_7261_CR2.jpg', 'Y', '2024-09-10 12:00:16', 1),
(12, 'Glute Pain', '', 'uploads/image/1726062963IMG_7237_CR2.jpg', 'Y', '2024-09-11 13:56:03', 1);
INSERT INTO `workout` (`id`, `routine_id`, `workout_title`, `workout_tipe`, `workout_desc`, `workout_type`, `equipments`, `rep`, `sets`, `worktime`, `image`, `video_type`, `url_type`, `video_path`, `status`, `update_on`, `workout_image_thumb`, `workout_level_id`, `musclegroup`, `min_calorie`, `max_calorie`, `week_no`, `day_no`) VALUES
(5, 6, 'Deadlift', 'If your grip is a limiting factor, consider using a mixed grip or lifting straps for heavier weights.', '<h4><strong>1. Set Up:</strong></h4>\r\n<ul>\r\n<li><strong>Barbell Position</strong>: Place the barbell on the ground in front of you. Stand with your feet about hip-width apart, with the barbell positioned over the middle of your feet (roughly where your shoelaces are).</li>\r\n<li><strong>Foot Placement</strong>: Your feet should be flat on the floor, and your toes can point slightly outward.</li>\r\n<li><strong>Grip the Bar</strong>: Bend at the hips and knees, and grip the bar just outside your knees. You can use an overhand grip (both palms facing you) or a mixed grip (one palm facing you and the other facing away) for added stability with heavier weights.</li>\r\n</ul>\r\n<h4><strong>2. Body Position:</strong></h4>\r\n<ul>\r\n<li><strong>Hinge at the Hips</strong>: Push your hips back as you bend down to grip the bar, keeping your chest up and your back flat. Your shoulders should be directly over the bar, and your head should be in a neutral position (looking slightly ahead).</li>\r\n<li><strong>Engage Your Core</strong>: Brace your core and engage your lats by pulling your shoulders slightly back, creating tension in your upper body.</li>\r\n</ul>\r\n<h4><strong>3. Lift the Barbell:</strong></h4>\r\n<ul>\r\n<li><strong>Start the Lift</strong>: Push through your heels and extend your hips and knees simultaneously to lift the bar off the ground. Keep the barbell close to your body, almost brushing against your shins and thighs.</li>\r\n<li><strong>Hips and Shoulders Together</strong>: Ensure your hips and shoulders rise at the same rate. Avoid shooting your hips up too fast, as this can compromise your back positioning.</li>\r\n<li><strong>Stand Tall</strong>: Once the bar passes your knees, stand tall by fully extending your hips and locking your knees. At the top, your body should be in a straight line with your chest lifted, core engaged, and glutes squeezed.</li>\r\n</ul>\r\n<h4><strong>4. Lower the Barbell:</strong></h4>\r\n<ul>\r\n<li><strong>Controlled Descent</strong>: Push your hips back to begin lowering the bar, keeping the bar close to your body. Bend your knees only once the bar reaches knee height.</li>\r\n<li><strong>Return to Start</strong>: Lower the bar back to the ground in a controlled manner, maintaining a flat back throughout.</li>\r\n</ul>\r\n<h4><strong>5. Repeat:</strong></h4>\r\n<ul>\r\n<li>Perform the desired number of repetitions, ensuring you maintain good form for every rep.</li>\r\n</ul>', '', 'Barbell', 5, 5, NULL, 'uploads/image/1723559780Screenshot_2024_08_13_at_15_34_29.png', 'URL', 'vimeo', '', 'Y', '2024-08-13 14:36:20', 'uploads/thumb/17235597801723559780screenshot_2024_08_13_at_15_34_29.png', 2, 'All Muscles Used', NULL, NULL, 1, 3),
(7, 6, 'Barbell backsquat', 'Keep your core braced at all times to protect your lower back.', '<h4><strong>1. Set Up:</strong></h4>\r\n<ul>\r\n<li><strong>Rack Position</strong>: Position the barbell at chest height on the squat rack. Stand under the bar with your feet shoulder-width apart.</li>\r\n<li><strong>Grip</strong>: Grip the bar with your hands slightly wider than shoulder-width apart. Ensure a firm grip, but not too tight.</li>\r\n<li><strong>Bar Position</strong>: Step under the bar and place it across your upper back, resting it on your traps or just below them on your rear deltoids (whichever is more comfortable for you).</li>\r\n<li><strong>Tighten Your Core</strong>: Engage your core muscles and keep your chest lifted.</li>\r\n</ul>\r\n<h4><strong>2. Unrack the Bar:</strong></h4>\r\n<ul>\r\n<li><strong>Lift the Bar</strong>: Stand up straight, lifting the bar off the rack while keeping your core tight and back straight.</li>\r\n<li><strong>Step Back</strong>: Take one or two steps back from the rack, positioning your feet shoulder-width apart with your toes slightly pointing outward.</li>\r\n</ul>\r\n<h4><strong>3. Perform the Squat:</strong></h4>\r\n<ul>\r\n<li><strong>Breathe in</strong>: Take a deep breath and brace your core.</li>\r\n<li><strong>Start Lowering</strong>: Initiate the squat by bending at your hips and knees simultaneously. Push your hips back as if you&rsquo;re sitting down into a chair. Keep your chest up and your spine neutral.</li>\r\n<li><strong>Depth</strong>: Lower yourself until your thighs are parallel to the floor (or slightly lower if your mobility allows). Your knees should track over your toes without caving inward.</li>\r\n<li><strong>Keep Weight Balanced</strong>: Ensure your weight is distributed evenly across your feet, especially pressing through your heels.</li>\r\n</ul>\r\n<h4><strong>4. Return to Starting Position:</strong></h4>\r\n<ul>\r\n<li><strong>Push Through Your Heels</strong>: Exhale as you push through your heels to stand back up. Extend your hips and knees simultaneously while keeping your core engaged and chest up.</li>\r\n<li><strong>Finish Strong</strong>: Once you\'re back to standing, squeeze your glutes at the top to complete the movement.</li>\r\n</ul>\r\n<h4><strong>5. Repeat:</strong></h4>\r\n<ul>\r\n<li>Perform the desired number of repetitions, maintaining form throughout the set.</li>\r\n</ul>', '', 'Barbell', 4, 8, NULL, 'uploads/image/1726131218Screenshot_2024_09_12_at_09_35_13.png', 'URL', 'vimeo', '', 'Y', '2024-09-12 08:53:38', 'uploads/thumb/17261312181726131218screenshot_2024_09_12_at_09_35_13.png', 2, 'All Muscles Used', NULL, NULL, 1, 3),
(8, 6, 'Copenhagen plank', 'If the full Copenhagen plank is too challenging, start by placing your knee (rather than your foot) on the bench. This reduces the load on the adductors.', '<h4><strong>1. Set Up:</strong></h4>\r\n<ul>\r\n<li><strong>Find a Bench or Elevated Surface</strong>: You&rsquo;ll need a sturdy surface like a bench or chair that is about knee-height.</li>\r\n<li><strong>Position Yourself</strong>: Lie on your side next to the bench, and place your top leg on the bench. Your foot, or the inside of your knee (for an easier version), should rest on the bench while your lower leg hovers off the ground.</li>\r\n<li><strong>Elbow Position</strong>: Prop yourself up on your forearm, with your elbow directly under your shoulder. Your body should form a straight line from your shoulders to your hips.</li>\r\n</ul>\r\n<h4><strong>2. Engage Your Core:</strong></h4>\r\n<ul>\r\n<li><strong>Lift Your Hips</strong>: Engage your core and squeeze your glutes as you lift your hips off the ground, bringing your body into a straight line. Your lower leg should remain off the floor.</li>\r\n<li><strong>Top Leg Supports Weight</strong>: The majority of your weight is supported by the top leg on the bench, with the adductors (inner thigh muscles) of this leg working hard to hold your body up.</li>\r\n</ul>\r\n<h4><strong>3. Hold the Plank Position:</strong></h4>\r\n<ul>\r\n<li><strong>Maintain a Straight Line</strong>: Your body should stay aligned from head to feet. Avoid letting your hips sag or rotate.</li>\r\n<li><strong>Keep Your Core Tight</strong>: Keep your core engaged to maintain stability and avoid shifting weight too much onto your elbow or shoulders.</li>\r\n</ul>\r\n<h4><strong>4. Switch Sides:</strong></h4>\r\n<ul>\r\n<li>After holding the plank for the desired duration (e.g., 20-30 seconds), slowly lower your hips back down and switch sides to work the other leg.</li>\r\n</ul>', '', 'Bench', 4, 8, NULL, 'uploads/image/1726131455Screenshot_2024_09_12_at_09_40_48.png', 'URL', 'vimeo', '', 'Y', '2024-09-12 08:57:35', 'uploads/thumb/17261314551726131455screenshot_2024_09_12_at_09_40_48.png', 2, 'Hip Adductors', NULL, NULL, 1, 2),
(9, 6, 'Dumbbell Lunge', 'Your step should be large enough to allow for a 90-degree angle at both the front and back knees when you lower yourself, but not so large that you lose balance.', '<h4><strong>1. Set Up:</strong></h4>\r\n<ul>\r\n<li><strong>Choose Your Dumbbells</strong>: Start with a pair of dumbbells that allow you to maintain proper form. Hold one in each hand with your arms fully extended by your sides, palms facing your body.</li>\r\n<li><strong>Stand Tall</strong>: Stand with your feet hip-width apart, and keep your chest up, shoulders back, and core engaged.</li>\r\n</ul>\r\n<h4><strong>2. Step Into the Lunge:</strong></h4>\r\n<ul>\r\n<li><strong>Step Forward</strong>: Take a controlled step forward with one foot. The size of the step should allow your front knee to be directly above your ankle when you lower yourself into the lunge. Your back foot should remain on the toes.</li>\r\n<li><strong>Lower Your Body</strong>: As you step forward, bend both knees to lower your body. Your front knee should form a 90-degree angle, and your back knee should hover just above the floor.</li>\r\n<li><strong>Keep Your Chest Up</strong>: Throughout the movement, maintain an upright posture, keeping your shoulders stacked over your hips and your chest lifted.</li>\r\n</ul>\r\n<h4><strong>3. Push Back to Starting Position:</strong></h4>\r\n<ul>\r\n<li><strong>Push Through the Front Heel</strong>: Press through the heel of your front foot to push yourself back to the starting position. As you rise, engage your glutes and quadriceps.</li>\r\n<li><strong>Stabilize</strong>: Keep your balance and avoid letting your knees collapse inward as you push back up.</li>\r\n</ul>\r\n<h4><strong>4. Alternate Legs:</strong></h4>\r\n<ul>\r\n<li>Repeat the lunge on the opposite leg, alternating sides for each rep.</li>\r\n</ul>\r\n<h4><strong>5. Repeat:</strong></h4>\r\n<ul>\r\n<li>Perform the desired number of repetitions, keeping your movements controlled and stable throughout.</li>\r\n</ul>', '', 'Dumbbells', 4, 6, NULL, 'uploads/image/1726131679Screenshot_2024_09_12_at_09_41_26.png', 'URL', 'vimeo', '', 'Y', '2024-09-12 09:01:19', 'uploads/thumb/17261316791726131679screenshot_2024_09_12_at_09_41_26.png', 2, 'Quadriceps', NULL, NULL, 1, 2),
(10, 6, 'Overhead Dummbell lunge', 'Keep your shoulders active and engaged to stabilize the dumbbells. Avoid shrugging your shoulders toward your ears.', '<h4><strong>1. Set Up:</strong></h4>\r\n<ul>\r\n<li><strong>Choose Your Dumbbells</strong>: Start with one or two light dumbbells. If using two dumbbells, hold one in each hand; if using one, hold it with both hands.</li>\r\n<li><strong>Raise the Dumbbell Overhead</strong>: Press the dumbbells overhead, fully extending your arms. Keep your biceps close to your ears and your shoulders down (away from your ears). Your palms should face each other, and your core should be braced to maintain stability.</li>\r\n<li><strong>Stand Tall</strong>: Stand with your feet hip-width apart, with your chest up and your core engaged.</li>\r\n</ul>\r\n<h4><strong>2. Step Into the Lunge:</strong></h4>\r\n<ul>\r\n<li><strong>Step Forward</strong>: Take a controlled step forward with one foot, just as you would with a regular lunge. The step should be large enough to ensure a 90-degree bend at both the front and back knees when you lower yourself.</li>\r\n<li><strong>Lower Your Body</strong>: Bend both knees to lower your body into the lunge. Your front knee should stay directly above your ankle, and your back knee should hover just above the floor.</li>\r\n<li><strong>Maintain Overhead Position</strong>: Throughout the lunge, keep your arms fully extended overhead and your core engaged to stabilize the weight. Avoid letting the dumbbells drift forward or backward.</li>\r\n</ul>\r\n<h4><strong>3. Push Back to Starting Position:</strong></h4>\r\n<ul>\r\n<li><strong>Push Through Your Front Heel</strong>: Press through the heel of your front foot to push yourself back to the starting position. Keep your balance and maintain the dumbbells overhead as you rise.</li>\r\n</ul>\r\n<h4><strong>4. Alternate Legs:</strong></h4>\r\n<ul>\r\n<li>Repeat the movement on the opposite leg, alternating legs for each rep.</li>\r\n</ul>\r\n<h4><strong>5. Repeat:</strong></h4>\r\n<ul>\r\n<li>Perform the desired number of repetitions on each side, ensuring controlled movement and stability throughout.</li>\r\n</ul>', '', 'Dumbbells', 4, 6, NULL, 'uploads/image/1726131795Screenshot_2024_09_12_at_09_32_57.png', 'URL', 'vimeo', '', 'Y', '2024-09-12 09:03:15', 'uploads/thumb/17261317951726131795screenshot_2024_09_12_at_09_32_57.png', 2, 'Glutes', NULL, NULL, 1, 1),
(11, 6, 'Overhead Squat', 'Ensure that your knees track over your toes and do not cave inward as you squat. Your feet should remain flat on the floor.', '<h4><strong>1. Set Up:</strong></h4>\r\n<ul>\r\n<li><strong>Choose Your Weight</strong>: You can use a barbell, dumbbell, or even a resistance band for beginners. Start light to focus on form. Hold the weight with both hands, extending it fully overhead.</li>\r\n<li><strong>Grip Position</strong>: If using a barbell, your hands should be wide enough to form a \"V\" shape when your arms are extended, just wider than shoulder-width. For dumbbells, hold one in each hand, or one with both hands, keeping your arms straight.</li>\r\n<li><strong>Raise the Weight Overhead</strong>: Press the barbell or dumbbells overhead with your arms fully extended. Your biceps should be close to your ears, and your shoulders should be actively engaged (without shrugging).</li>\r\n<li><strong>Stand Tall</strong>: Keep your feet shoulder-width apart or slightly wider, with your toes pointing slightly outward. Engage your core to maintain stability.</li>\r\n</ul>\r\n<h4><strong>2. Perform the Squat:</strong></h4>\r\n<ul>\r\n<li><strong>Engage Your Core</strong>: Before beginning the squat, brace your core to stabilize your spine and maintain the weight overhead.</li>\r\n<li><strong>Initiate the Squat</strong>: Begin the squat by pushing your hips back and bending your knees simultaneously. Keep your chest up and your torso as upright as possible.</li>\r\n<li><strong>Lower Your Body</strong>: Squat down until your thighs are parallel to the floor, or lower if your mobility allows. Your knees should track over your toes, and the weight should stay directly overhead.</li>\r\n<li><strong>Maintain Overhead Position</strong>: Keep the weight balanced above you with your arms locked out. Avoid letting the weight drift forward or backward. Your upper back and shoulders should remain engaged to stabilize the load.</li>\r\n</ul>\r\n<h4><strong>3. Return to Standing Position:</strong></h4>\r\n<ul>\r\n<li><strong>Drive Through Your Heels</strong>: Press through your heels to stand back up, extending your hips and knees simultaneously. As you rise, keep your core tight and the weight stable overhead.</li>\r\n<li><strong>Stand Tall</strong>: At the top of the movement, your body should be in a straight line with the weight directly overhead.</li>\r\n</ul>\r\n<h4><strong>4. Repeat:</strong></h4>\r\n<ul>\r\n<li>Perform the desired number of repetitions, maintaining control and stability throughout each squat.</li>\r\n</ul>', '', 'Barbell', 4, 8, NULL, 'uploads/image/1726131909Screenshot_2024_09_12_at_09_37_42.png', 'URL', 'vimeo', '', 'Y', '2024-09-12 09:05:09', 'uploads/thumb/17261319091726131909screenshot_2024_09_12_at_09_37_42.png', 2, 'Full Body', NULL, NULL, 1, 1),
(12, 5, 'Dummbell Thrusters', 'Use the upward momentum from the squat to help power the dumbbells overhead. This makes the exercise a full-body, explosive movement.', '<h4><strong>1. Set Up:</strong></h4>\r\n<ul>\r\n<li><strong>Choose Your Dumbbells</strong>: Pick a pair of dumbbells that you can comfortably press overhead. Hold one dumbbell in each hand at shoulder height, with your palms facing inward (neutral grip) and your elbows bent.</li>\r\n<li><strong>Stand Tall</strong>: Your feet should be shoulder-width apart or slightly wider, with your toes pointing slightly outward. Keep your chest up and engage your core.</li>\r\n</ul>\r\n<h4><strong>2. Perform the Squat:</strong></h4>\r\n<ul>\r\n<li><strong>Initiate the Squat</strong>: Begin by pushing your hips back and bending your knees to lower into a squat. Keep your chest upright, core engaged, and back neutral.</li>\r\n<li><strong>Squat Depth</strong>: Lower your body until your thighs are parallel to the floor (or as low as your mobility allows). Make sure your knees track over your toes, and your weight is balanced over your heels.</li>\r\n</ul>\r\n<h4><strong>3. Explode Into the Press:</strong></h4>\r\n<ul>\r\n<li><strong>Drive Through Your Heels</strong>: Push through your heels and extend your legs to return to standing, driving upward with power.</li>\r\n<li><strong>Press Overhead</strong>: As you rise from the squat, use the momentum to press the dumbbells overhead. Fully extend your arms so the dumbbells are directly above your shoulders, with your biceps close to your ears.</li>\r\n</ul>\r\n<h4><strong>4. Lower the Dumbbells:</strong></h4>\r\n<ul>\r\n<li><strong>Return to Starting Position</strong>: After pressing the dumbbells overhead, lower them back to shoulder height in a controlled manner while simultaneously preparing to descend into the next squat.</li>\r\n</ul>\r\n<h4><strong>5. Repeat:</strong></h4>\r\n<ul>\r\n<li>Immediately go into the next repetition by squatting down again, then driving up into another press. Perform the desired number of repetitions.</li>\r\n</ul>', '', 'Dumbbells', 3, 8, NULL, 'uploads/image/1726132133Screenshot_2024_09_12_at_09_38_01.png', 'URL', 'vimeo', '', 'Y', '2024-09-12 09:08:53', 'uploads/thumb/17261321331726132133screenshot_2024_09_12_at_09_38_01.png', 2, 'Quadriceps', NULL, NULL, 2, 3),
(13, 5, 'Zombie Squat', 'Start light, this can be challenging but is great for trunk bracing to progress to front squats', '<p>Arms extended out forward, keep back neutral, perform a squat to fullest range possible keeping knees tracking toes, heels flat with even weight distribution and arms in place</p>', '', 'Barbell', -5, -4, NULL, 'uploads/image/1728050080Screenshot_3.png', 'URL', 'server', 'https://studio.youtube.com/channel/UCKeaJIQ_phvxWW5tTQ_oAhQ/videos/upload?filter=%5B%5D&sort=%7B%22columnType%22%3A%22date%22%2C%22sortOrder%22%3A%22DESCENDING%22%7D', 'Y', '2024-10-04 13:54:40', 'uploads/thumb/17280500801728050080screenshot_3.png', 2, 'All Muscles Used', NULL, NULL, 2, 3),
(14, 5, 'Wide Grip Pull up', 'Resistance bands can be used to reduce load, or regression to inverted row', '<p>Keeping shoulders both back and down, take hands to snatch grip or there abouts, pull chin to the bar with body in a braced hang.</p>', '', 'Resistance bands', 8, 4, NULL, 'uploads/image/1728050630Screenshot_4.png', 'URL', 'server', 'https://youtu.be/lHvokhXk_Bg', 'Y', '2024-10-04 14:03:50', 'uploads/thumb/17280506301728050630screenshot_4.png', 2, 'Back', NULL, NULL, 2, 2),
(15, 5, 'Wall sit', 'avoid using the arms as an aid or slumping body to low or raising too high', '<p>using a wall, lower body to a 90 degree bend at the knee, same at the ankle and same at the hip with the back flat to the wall hold inn position</p>', '', 'Bodyweight, Bodyweight', 0, 1, NULL, 'uploads/image/1728051476Screenshot_6.png', 'URL', 'server', 'https://youtu.be/W4WzhhUweyM', 'Y', '2024-10-04 14:17:56', 'uploads/thumb/17280514761728051476screenshot_6.png', 2, 'Quadriceps', NULL, NULL, 2, 1),
(16, 5, 'Thorasic crocodile stretch', 'Perform to a range that is comfortable for you', '<p>from a press up position take one foot to the outside of the hand on the same side, drop the opposing knee down to the floor and if possible the opposite forearm to the floor, the hand thats closest to the foot grab the back of the neck, take thew same elbow as close to the floor as possible and raise the elow to the ceiling as high as possible.</p>', '', 'Bodyweight', 20, 2, NULL, 'uploads/image/1728053843Screenshot_8.png', 'URL', 'server', 'https://youtu.be/uf6uyX5Ysr8', 'Y', '2024-10-04 14:57:23', 'uploads/thumb/17280538431728053843screenshot_8.png', 2, 'All Muscles Used', NULL, NULL, 2, 1),
(17, 5, 'Teddybear roll', 'Keep to a range that is manageable', '<p>laying back flat to the floor, roll the legs over the head to reach the floor as close as whats comfortable, then reverse and spread the legs rfeaching as far forward as whats comfortable.</p>', '', 'Bodyweight, Bodyweight', 0, 0, NULL, 'uploads/image/1728573414Screenshot_10.png', 'URL', 'server', 'https://youtu.be/3e3iE3dLQKs', 'Y', '2024-10-10 15:16:54', 'uploads/thumb/17285734141728573414screenshot_10.png', 2, 'All Muscles Used', NULL, NULL, 1, 3),
(18, 5, 'Sumo High Pull', 'action of the arms should be similar to zipping up a coat', '<p>startingf and finishing In a sumo frog stance, keep arms close to the body and pull up to high chest, keep elbows high and back flat</p>', '', 'Barbell', 3, 15, NULL, 'uploads/image/1728573710Screenshot_12.png', 'URL', 'server', '', 'Y', '2024-10-10 15:21:50', 'uploads/thumb/17285737101728573710screenshot_12.png', 2, 'All Muscles Used', NULL, NULL, 1, 3),
(19, 5, 'Strict toes to bar/Knees to chest', 'keep motion slow and controlled to avoid a swing', '<p>From a bar dead-hang position, keeping legs straight, pull the legs as close to the bar as strength will allow. Knees to chest as as toes to bar but a smaller lever by bending the knees and tucking the knees as close to the chest as you can.</p>', '', 'Bodyweight, Bodyweight', 3, 15, NULL, 'uploads/image/1728574100Screenshot_13.png', 'URL', 'server', 'https://youtu.be/0lqUdNL_5uM', 'Y', '2024-10-10 15:28:20', 'uploads/thumb/17285741001728574100screenshot_13.png', 2, 'Abdominals', NULL, NULL, 1, 2),
(20, 5, 'Stranding tricep extension', 'try not to flair elbows to the side or bow the back', '<p>In standing position, hands narrow grip, arms extended up overhead, keep elbows pointing forward in position, flex forearm at the elbow back as fas as comfortable</p>', '', 'Barbell', 3, 12, NULL, 'uploads/image/1728575094Screenshot_14.png', 'URL', 'server', 'https://youtu.be/Nqscz0isSnI', 'Y', '2024-10-10 15:44:54', 'uploads/thumb/17285750941728575094screenshot_14.png', 2, 'Triceps', NULL, NULL, 1, 2),
(21, 5, 'Snatch grip deadlift', 'this exercise will put extra pressure on upper body strength to hold a neutral deadlift position', '<p>in overhead squat grip with hands perform a deadlift maintaining the standard deadlift position</p>', '', 'Barbell', 5, 5, NULL, 'uploads/image/1728580814Screenshot_15.png', 'URL', 'server', 'https://youtu.be/1xfmhqlVN_I', 'Y', '2024-10-10 17:20:14', 'uploads/thumb/17285808141728580814screenshot_15.png', 4, 'All Muscles Used', NULL, NULL, 1, 1),
(22, 5, 'Skull crusher', 'don\'t let elbows flair, must maintain control to the head.', '<p>in a laid bench position, hands slightly narrow of the shoulders, lock elbows in vertical overhead position, lfex the elbow allowing forearms to travel just above the forehead.</p>', '', 'Barbell', 0, 0, NULL, 'uploads/image/1728581333Screenshot_16.png', 'URL', 'server', 'https://youtu.be/rhxzU1CX6Ig', 'Y', '2024-10-10 17:28:53', 'uploads/thumb/17285813331728581333screenshot_16.png', 2, 'Triceps', NULL, NULL, 1, 2),
(23, 5, 'Butterly sit up', 'an ab mat can be used.', '<p>Soles of the feet together, seating on the floor, reach for the floor behind the head and then for the floor in front of the feet.</p>', '', 'Bodyweight, Bodyweight', 0, 0, NULL, 'uploads/image/1728582572Screenshot_17.png', 'URL', 'server', 'https://youtu.be/RSpIS8ehyVs', 'Y', '2024-10-10 17:49:33', 'uploads/thumb/17285825721728582572screenshot_17.png', 2, 'Abdominals', NULL, NULL, 1, 1),
(24, 11, 'Sissy squat', 'keep within your own range.', '<p>Using a barbell for support/saftey, hinge at the knee maintaining a straight line from knee to shoulder, heels will come of the floor</p>', '', 'Bodyweight, Bodyweight', 0, 0, NULL, 'uploads/image/1728582956Screenshot_18.png', 'URL', 'server', 'https://youtu.be/OXriHQ9Pc8I', 'Y', '2024-10-10 17:55:57', 'uploads/thumb/17285829561728582956screenshot_18.png', 4, 'Quadriceps', NULL, NULL, 2, 3),
(25, 11, 'Single leg Glute bridge', 'if this is too difficult use both legs', '<p>back flat to the floor, straighten one leg with the other in a bend and the foot flat to the floor, push through the floor with the bent leg hovering the straight leg off the floor, keep hips level and foot flat as you push.</p>', '', 'Bodyweight, Bodyweight', 3, 6, NULL, 'uploads/image/1728584271Screenshot_20.png', 'URL', 'server', 'https://youtu.be/d5pFYMNQZ2s', 'Y', '2024-10-10 18:17:51', 'uploads/thumb/17285842711728584271screenshot_20.png', 2, 'Glutes', NULL, NULL, 2, 3),
(26, 11, 'Dumbell single arm push press', 'keep eyes on a point of focus for balance.', '<p>Keep wrist straight and using a leg drive, push from a single arm front rack position to an overhead position with no assistance from opposite arm.</p>', '', 'Dumbbells', 10, 3, NULL, 'uploads/image/1728584633Screenshot_21.png', 'URL', 'server', 'https://youtu.be/EyctVhOTRSY', 'Y', '2024-10-10 18:23:53', 'uploads/thumb/17285846331728584633screenshot_21.png', 2, 'Shoulders', NULL, NULL, 2, 2),
(27, 11, 'Dumbell box step up/over', 'keep to a steady pace for saftey', '<p>holding a dumbell by your side, step up onto the box showing control to a fully extended position. step-over include a lowering phase at the other side of the box again maintaining control</p>', '', 'Dumbbells', 12, 3, NULL, 'uploads/image/1728584981Screenshot_22.png', 'URL', 'server', 'https://youtu.be/aiVXEIGSWuM', 'Y', '2024-10-10 18:29:41', 'uploads/thumb/17285849811728584981screenshot_22.png', 2, 'Full Body', NULL, NULL, 2, 2),
(28, 11, 'Single arm row', 'Slight rotation of the upper back can occur, this is fine as there is no virtical loading through the spine.', '<p>one knee and hand resting on a bench for support, back flat and let weighted arm hang directly under shoulder, row DB into side with as full range as possible.</p>', '', 'Dumbbells', 10, 3, NULL, 'uploads/image/1729515645Screenshot_23.png', 'URL', 'server', 'https://youtu.be/KUXN0uBoQ0o', 'Y', '2024-10-21 13:00:45', 'uploads/thumb/17295156451729515645screenshot_23.png', 2, 'Back', NULL, NULL, 2, 1),
(29, 11, 'Single arm overhead lunge', 'slow is smooth and smooth is fast', '<p>Arm locked out overhead, with no assistance from unloaded arm lunge forward until back knee touches the floor with control, maintaning the overhead position throughout.</p>', '', 'Dumbbells', 4, 8, NULL, 'uploads/image/1729516484Screenshot_24.png', 'URL', 'server', 'https://youtu.be/cUYSUIhl9Wg', 'Y', '2024-10-21 13:14:44', 'uploads/thumb/17295164841729516484screenshot_24.png', 4, 'All Muscles Used', NULL, NULL, 2, 1),
(30, 11, 'Side clam', 'place hand on hip bone to help avoid rotation during leg motion', '<p>In a side plank position create a straight line from knee to shoulder,&nbsp; forearm resting on the floor with elbow directly underneath the shoulder, one knee ontop of the other with all lower legs behid the body, rotate the knees open only hinging at the hip jpoint with no other motion&nbsp;</p>', '', 'Bodyweight, Bodyweight', 3, 8, NULL, 'uploads/image/1729516924Screenshot_25.png', 'URL', 'server', '', 'Y', '2024-10-21 13:22:04', 'uploads/thumb/17295169241729516924screenshot_25.png', 2, 'Glutes', NULL, NULL, 1, 3),
(31, 11, 'side plank', 'try not to rotate and round the upper back', '<p>body side on, legs hips and shoulders stacked and inline hold and maintain position with forarm to the floor and elbow directly under the shoulder. this can be performed from the knee rather than feet</p>', '', 'Bodyweight, Bodyweight', 2, 2, NULL, 'uploads/image/1729518455Screenshot_26.png', 'URL', 'server', '', 'Y', '2024-10-21 13:47:35', 'uploads/thumb/17295184551729518455screenshot_26.png', 2, 'All Muscles Used', NULL, NULL, 1, 2),
(32, 11, 'Shoulder windmill stretch', 'progress by bringing your grip closer', '<p>arms wide and elbows locked, shouldersa back and with a light band or pipe, swing around the body in as full a range as comfortable</p>', '', 'Resistance bands', 20, 2, NULL, 'uploads/image/1729519042Screenshot_27.png', 'URL', 'server', '', 'Y', '2024-10-21 13:57:22', 'uploads/thumb/17295190421729519042screenshot_27.png', 2, 'Shoulders', NULL, NULL, 1, 3),
(33, 11, 'Shoulder nerve floss', 'keep to a range that\'s comfortable', '<p>Place hand with finger facings down and palm as flat to the wall as possible with the arm in a straight line to the shoulder. With shoulders back and down pivote the head away from the wall to a comfortable intensity of stretch</p>', '', 'Bodyweight, Bodyweight', 20, 2, NULL, 'uploads/image/1729519377Screenshot_28.png', 'URL', 'server', '', 'Y', '2024-10-21 14:02:57', 'uploads/thumb/17295193771729519377screenshot_28.png', 2, 'Shoulders', NULL, NULL, 1, 2),
(34, 11, 'Seated Military press', 'dont be tempted to arch your back during press', '<p>Using a barbell and sittingb on a bench perform a strict press from shoulders to locked overhead position</p>', '', 'Barbell', 10, 4, NULL, 'uploads/image/1730389433Screenshot_29.png', 'URL', 'server', 'https://youtu.be/hXPVNWngxnQ', 'Y', '2024-10-31 15:43:53', 'uploads/thumb/17303894331730389433screenshot_29.png', 2, 'Shoulders', NULL, NULL, 1, 1),
(35, 11, 'Seated Goodmorning', 'keep weight light enough as not to compromise back position', '<p>sitting on a bench, prefferably staddling, with bar on back rack, hinge at the hip untill a stretch in the hamstring is felt keeping the back position neutral&nbsp;</p>', '', 'Barbell', 0, 0, NULL, 'uploads/image/1730389771Screenshot_30.png', 'URL', 'server', '', 'Y', '2024-10-31 15:49:31', 'uploads/thumb/17303897711730389771screenshot_30.png', 4, 'All Muscles Used', NULL, NULL, 1, 1),
(36, 8, 'Seated Dumbell Shoulder Press', 'a bench with a back rest can be bused if position is hard to maintain', '<p>In a sested position on a bench, take dum bells from shoulder to a locked overhead position, keep back straight, wrists neutral and forearms straight.</p>', '', 'Dumbbells', 10, 4, NULL, 'uploads/image/1730390862Screenshot_31.png', 'URL', 'server', 'https://youtu.be/oB8j0YRppqQ', 'Y', '2024-10-31 16:07:42', 'uploads/thumb/17303908621730390862screenshot_31.png', 2, 'Shoulders', NULL, NULL, 2, 3),
(37, 8, 'Rotator cuff sleeper stretvch', 'dont push the stretch too much as these are delicate muscles.', '<p>laying on your side,squeeze shoulders back anmd down, with bottom side elbowon the floor level with shoulders bend to 90 degrees at the elbow and gently push the forearm using other hand, rotating the arm to the point of stretch.</p>', '', 'Bodyweight, Bodyweight', 2, 2, NULL, 'uploads/image/1730391257Screenshot_32.png', 'URL', 'server', 'https://youtu.be/Q9qfsIjaIhs', 'Y', '2024-10-31 16:14:17', 'uploads/thumb/17303912571730391257screenshot_32.png', 3, 'Shoulders', NULL, NULL, 2, 2),
(38, 8, 'Preacher external rotation stretch', 'can be held or done in reps', '<p>Place elbows on the end of a bench, using a pipe grip slightly wider than shoulder width and place elbows slightly narrower, drop the head and torso as low as possible until a stretch is felt, then move pipe as close the the shoulders.</p>', '', 'Bodyweight, Bodyweight', 20, 2, NULL, 'uploads/image/1730391674Screenshot_33.png', 'URL', 'server', 'https://youtu.be/O2BV7nPazXU', 'Y', '2024-10-31 16:21:14', 'uploads/thumb/17303916741730391674screenshot_33.png', 3, 'Shoulders', NULL, NULL, 2, 2),
(39, 8, 'Romanian deadlift', 'don\'t reach for the floor and keep to a range where you don\'t lose position', '<p>Using a conventional deadlift techneque to take thne bar to a locked standing position, slightly bend the knees, keep shoulders back anmd down anjd arms loose, hinge at the hip with weight evenly spread through the foot, until a stretch at the hamstring can be felt.</p>', '', 'Barbell', 12, 3, NULL, 'uploads/image/1730392290Screenshot_34.png', 'URL', 'server', 'https://youtu.be/_HYRrfOfG80', 'Y', '2024-10-31 16:31:30', 'uploads/thumb/17303922901730392290screenshot_34.png', 3, 'Hamstrings', NULL, NULL, 2, 1),
(40, 8, 'Pancake groin stretch', 'relax and try to keep knees straight', '<p>Sitting on the floor, wioth legs straight open up the thighs as wide as posible until a stretch is felt inm the groin, keep back as flat as possible</p>', '', 'Bodyweight, Bodyweight', 1, 2, NULL, 'uploads/image/1730392616Screenshot_35.png', 'URL', 'server', 'https://youtu.be/FZV3kZk2d1E', 'Y', '2024-10-31 16:36:56', 'uploads/thumb/17303926161730392616screenshot_35.png', 2, 'Hip Adductors', NULL, NULL, 2, 1),
(41, 8, 'Roll out', 'maintain position above trying to achieve range', '<p>from a kneelinbg press up position, brace trunk into solid position and roll the bar forward maintaining positioning and shoulderws down and back,&nbsp; hinging only at the shoulder joint</p>', '', 'Barbell', 5, 4, NULL, 'uploads/image/1730392932Screenshot_36.png', 'URL', 'server', 'https://youtu.be/q-OEdC88KVg', 'Y', '2024-10-31 16:42:12', 'uploads/thumb/17303929321730392932screenshot_36.png', 4, 'Abdominals', NULL, NULL, 1, 3),
(42, 8, 'Reverse plank', 'Using two benches, position them so ankles are resting on one and shoulders, neck and head on the other, keep body in a braced straight line similar to a plank facing the ceiling.', '', '', 'Bodyweight, Bodyweight', 1, 3, NULL, 'uploads/image/1730397427Screenshot_37.png', 'URL', 'server', 'https://youtu.be/O8ElXXitL5I', 'Y', '2024-10-31 17:57:07', 'uploads/thumb/17303974271730397427screenshot_37.png', 2, 'Back', NULL, NULL, 2, 1),
(43, 8, 'Reverse nordic curl', 'don\'t allow the hips to hinge, bands can be used to assist', '<p>in a knealing position, hinge at the knee backwards as far as you can until a quad stretch is felt</p>', '', 'Bodyweight, Bodyweight', 5, 4, NULL, 'uploads/image/1730397698Screenshot_38.png', 'URL', 'server', 'https://youtu.be/eM3zGHIH8Oc', 'Y', '2024-10-31 18:01:39', 'uploads/thumb/17303976981730397698screenshot_38.png', 3, 'Quadriceps', NULL, NULL, 1, 3),
(44, 8, 'Renegade row', 'this can also be done from the knees rather than the feet.', '<p>in a plank position, holding on to dumbells, row one into the sided maintaining the plank position then alternate to other arm</p>', '', 'Dumbbells', 10, 4, NULL, 'uploads/image/1730398270Screenshot_39.png', 'URL', 'server', 'https://youtu.be/JKe2eTBQGVw', 'Y', '2024-10-31 18:11:10', 'uploads/thumb/17303982701730398270screenshot_39.png', 2, 'All Muscles Used', NULL, NULL, 1, 2),
(45, 8, 'Plank', 'can be regressed to the knees', '<p>in a press up osistion, hands directly under shoulders, hold a straight line position</p>', '', 'Bodyweight, Bodyweight', 1, 3, NULL, 'uploads/image/1730398919Screenshot_40.png', 'URL', 'server', 'https://youtu.be/aVFNuuiIna0', 'Y', '2024-10-31 18:21:59', 'uploads/thumb/17303989191730398919screenshot_40.png', 2, 'Abdominals', NULL, NULL, 1, 2),
(46, 8, 'Pigeon stretch', 'a bench can be used for the leg in stretch', '<p>Take front shin across the body with both hands to the floor, reach the back leg as far as comfortable, try and get the knee of the front leg close to 90 degree angle</p>', '', 'Bodyweight, Bodyweight', 1, 2, NULL, 'uploads/image/1730444535Screenshot_41.png', 'URL', 'server', 'https://youtu.be/P0Fam2m7xf0', 'Y', '2024-11-01 07:02:15', 'uploads/thumb/17304445351730444535screenshot_41.png', 2, 'Glutes', NULL, NULL, 1, 1),
(47, 8, 'Pendelay row', 'If the position is a struggle take the exercise to a bent over row, keep weight evenly spread through the feet', '<p>from a deadlift start position row the bar into the low chest, keeping the back and hips in position with no movement, keep forearms as close to vertical.</p>', '', 'Barbell', 8, 4, NULL, 'uploads/image/1730445181Screenshot_42.png', 'URL', 'server', 'https://youtu.be/F_xVpFY2GrM', 'Y', '2024-11-01 07:13:02', 'uploads/thumb/17304451811730445181screenshot_42.png', 3, 'Back', NULL, NULL, 1, 1),
(48, 10, 'GHD back extension', 'GHD machines can be used', '<p>using an achor point to keep legs in place, hip bone on the edge of a bench, extend the back through full range of motion</p>', '', 'Bodyweight, Bodyweight', 12, 4, NULL, 'uploads/image/1730446110Screenshot_43.png', 'URL', 'server', 'https://youtu.be/SccQKh3kD9E', 'Y', '2024-11-01 07:28:30', 'uploads/thumb/17304461101730446110screenshot_43.png', 2, 'All Muscles Used', NULL, NULL, 2, 3),
(49, 10, 'Trap (upper) Stretch', 'try not to twist the neck', '<p>sit on the palm of the hand of the side thats being stretched, with the opposite arm, gently pull the head aiming for the opposite knee, keep shoulders back and down until a slight stretch is felt&nbsp;</p>', '', 'Bodyweight, Bodyweight', 1, 2, NULL, 'uploads/image/1730446476Screenshot_44.png', 'URL', 'server', 'https://youtu.be/i2t_72uadB0', 'Y', '2024-11-01 07:34:36', 'uploads/thumb/17304464761730446476screenshot_44.png', 2, 'Traps', NULL, NULL, 2, 1),
(50, 10, 'Muscle snatch', 'keep the elbows as high as possible before the turn over of the bar', '<p>with the hands in a snatch grip and in a deadlift start position, in one fast motion take the bar to a locked overhead position, making sure the back stays neutral and the bar is kept close the the body</p>', '', 'Barbell', 5, 4, NULL, 'uploads/image/1730446814Screenshot_45.png', 'URL', 'server', 'https://youtu.be/CDnTJKkJSKE', 'Y', '2024-11-01 07:40:14', 'uploads/thumb/17304468141730446814screenshot_45.png', 2, 'All Muscles Used', NULL, NULL, 2, 2),
(51, 10, 'Military press', 'Switch to dumbbells if the front rack position causes discomfort.', '<ol style=\"color: #0e101a; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\">\r\n<li style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt; list-style-type: decimal;\"><strong style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\">Set Up in Front Rack:</strong><span style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\" data-preserver-spaces=\"true\"> Start with the barbell in the front rack position, maintaining a straight back and locked knees.</span></li>\r\n<li style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt; list-style-type: decimal;\"><strong style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\">Barbell Position:</strong><span style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\" data-preserver-spaces=\"true\"> Keep the barbell close to your body, with your forearms vertical and wrists straight.</span></li>\r\n<li style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt; list-style-type: decimal;\"><strong style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\">Press Upward:</strong><span style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\" data-preserver-spaces=\"true\"> Press the barbell overhead, fully extending your arms until the bar is locked.</span></li>\r\n<li style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt; list-style-type: decimal;\"><strong style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\">Maintain Posture:</strong><span style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\" data-preserver-spaces=\"true\"> Keep your core engaged throughout the movement and avoid leaning back.</span></li>\r\n<li style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt; list-style-type: decimal;\"><strong style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\">Return and Repeat:</strong><span style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\" data-preserver-spaces=\"true\"> Lower the barbell with control and repeat for the desired reps.</span></li>\r\n</ol>', '', 'Barbell', 10, 4, NULL, 'uploads/image/1730458255Screenshot_46.png', 'URL', 'server', 'https://youtu.be/vr9-PscLork', 'Y', '2024-11-01 10:50:55', 'uploads/thumb/17304582551730458255screenshot_46.png', 2, 'Shoulders', NULL, NULL, 2, 1),
(52, 10, 'Hammer curl', 'This movement can also be performed while seated.', '<ol style=\"color: #0e101a; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\">\r\n<li style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt; list-style-type: decimal;\"><strong style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\">Stand Tall:</strong><span style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\" data-preserver-spaces=\"true\"> Begin in a standing position with a dumbbell in each hand, palms facing toward your torso.</span></li>\r\n<li style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt; list-style-type: decimal;\"><strong style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-top: 0pt; margin-bottom: 0pt;\">Elbow Position:</strong><span style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-or